// Merge all context data into ctx pattern
// Input 0 has ctx from Merge Conversation ctx
const ctxInput = $input.all().find(item => item.json.ctx);
const ctx = ctxInput?.json?.ctx || {};

// Get all merged inputs
const allInputs = $input.all();

// Extract conversation history and sort by timestamp ascending
const historyItems = allInputs.filter(item => item.json.role !== undefined && item.json.text !== undefined);
const history = historyItems
  .sort((a, b) => new Date(a.json.timestamp) - new Date(b.json.timestamp))
  .map(item => ({
    role: item.json.role,
    text: item.json.text
  }));

// Extract north star
let northStar = 'Not set';
const northStarItem = allInputs.find(item => item.json.value !== undefined && item.json.key === undefined);
if (northStarItem) {
  northStar = northStarItem.json.value;
}

// Extract activities
const activities = allInputs
  .filter(item => item.json.category && item.json.description)
  .map(item => ({
    timestamp: item.json.timestamp,
    category: item.json.category,
    description: item.json.description
  }));

// Extract notes
const notes = allInputs
  .filter(item => item.json.category && item.json.text && !item.json.role)
  .map(item => ({
    timestamp: item.json.timestamp,
    category: item.json.category,
    text: item.json.text
  }));

return [{
  json: {
    ctx: {
      ...ctx,
      llm: {
        ...ctx.llm,
        north_star: northStar,
        inference_start: Date.now(),
        history: history,
        activities: activities,
        notes: notes
      }
    }
  }
}];

// Prepare trace data after LLM inference
// ctx comes from Merge LLM Result
const ctx = $json.ctx || {};
const response = ctx.llm?.completion_text || '';
const inferenceStart = ctx.llm?.inference_start || Date.now();
const durationMs = Date.now() - inferenceStart;

// Build filled prompt (reconstructed from template)
const promptText = `You are an AI life coach helping the user reflect, plan, and think deeply.

## Context Available

**User's Guiding Principle:** ${ctx.llm?.north_star || '(not set)'}

**Recent Activities (Last 20)**
${(ctx.llm?.activities || []).map(act => `${act.timestamp} - ${act.description}`).join('\n')}

**Recent Notes (Last 10)**
${(ctx.llm?.notes || []).map(n => `[${n.category}] ${n.text}`).join('\n')}

## Instructions
...

## Conversation History
${(ctx.llm?.history || []).map(msg => `${msg.role.toUpperCase()}: ${msg.text}`).join('\n\n')}

## Current Message
The user just said: "${ctx.event?.clean_text || ''}"

Respond to their message now.`;

// Format trace_chain for PostgreSQL uuid[] type
const traceChain = ctx.event?.trace_chain || [];
const traceChainPg = '{' + traceChain.join(',') + '}';

return [{
  json: {
    ctx: ctx,
    // Trace data for query
    input_text: ctx.event?.clean_text || '',
    input_thread_id: ctx.event?.thread_id,
    input_history_length: (ctx.llm?.history || []).length,
    prompt_text: promptText,
    completion_text: response,
    result_response_length: response.length,
    result_referenced_activities: (ctx.llm?.activities || []).length,
    result_referenced_notes: (ctx.llm?.notes || []).length,
    duration_ms: durationMs,
    event_id: ctx.event?.event_id,
    trace_chain_pg: traceChainPg
  }
}];
