const json = $input.first().json;
const content = (json.body.content || "").trim();
const lowerContent = content.toLowerCase();

const TAG_TABLE = [
  ["!!", "act"],
  ["..", "note"],
  ["++", "chat"],
  ["--", "save"],
  ["::", "cmd"],
  ["$$", "todo", "to-do"]
];

let tag = null;
let clean_text = content;

for (const row of TAG_TABLE) {
  const normalized = row[0];

  const match = row.find(alias => {
    const a = alias.toLowerCase();

    // Check if alias is strictly symbols (no letters or numbers)
    const isSymbolOnly = !/[a-z0-9]/i.test(a);

    if (isSymbolOnly) {
      // Symbols match even if glued to text: "!!task"
      return lowerContent.startsWith(a);
    } else {
      // Words require a space or exact match: "act task" or "act"
      return lowerContent.startsWith(a + " ") || lowerContent === a;
    }
  });

  if (match) {
    tag = normalized;
    clean_text = content.slice(match.length).trim();
    break;
  }
}

return [{ json: { ...json.body, tag, clean_text } }];

const cleanText = $json.clean_text || "";
const tag = $json.tag;

// Tier 1: Empty/whitespace (NO TAG)
if (!cleanText.trim() && !tag) {
  return {
    json: {
      ctx: {
        webhook_data: $json,
        validation: { result: "block" }
      }
    }
  };
}

// Tier 2: Tag-only (HAS TAG but no content)
if (tag && cleanText.trim() === "") {
  return {
    json: {
      ctx: {
        webhook_data: $json,
        validation: { result: "warn" }
      }
    }
  };
}

// Tier 3: Test keywords
const testKeywords = /\b(test|testing|aaaaa)\b/i;
if (testKeywords.test(cleanText)) {
  return {
    json: {
      ctx: {
        webhook_data: $json,
        validation: { result: "no_projections" }
      }
    }
  };
}

// Tier 4: Continue
return {
  json: {
    ctx: {
      webhook_data: $json,
      validation: { result: "continue" }
      }
  }
};

// Build DB query context for Execute_Queries
// Prepares INSERT query for discord_message event
const parsed = $input.first().json;
const webhook_data = parsed.ctx?.webhook_data || parsed;

// Build parameters array
const params = [
    webhook_data.guild_id,
    webhook_data.channel_id,
    webhook_data.message_id,
    `https://discord.com/channels/${webhook_data.guild_id}/${webhook_data.channel_id}/${webhook_data.message_id}`,
    webhook_data.author?.login || webhook_data.author_login,
    webhook_data.thread_id || null,
    webhook_data.content,
    webhook_data.clean_text,
    webhook_data.tag || null,
    webhook_data.timestamp
];

const db_queries = [{
        key: 'message_event',
        sql: `INSERT INTO events (
  event_type,
  payload,
  idempotency_key
) VALUES (
  'discord_message',
  jsonb_build_object(
    'content', $7,
    'clean_text', $8,
    'tag', $9,
    'discord_guild_id', $1,
    'discord_channel_id', $2,
    'discord_message_id', $3,
    'message_url', $4,
    'author_login', $5,
    'thread_id', $6,
    'timestamp', $10::timestamptz
  ),
  $3
)
ON CONFLICT (event_type, idempotency_key) DO UPDATE
SET payload = EXCLUDED.payload
RETURNING *;`,
        params: params
      }];

// Return ctx with db_queries array (Execute_Queries pattern)
return [{
  json: {
    ctx: {
      ...parsed.ctx,
      db_queries
    }
  }
}];

