const categories = $input.all().map(item => item.json.name).join(', ');
const event = $('Execute Workflow Trigger').first().json.event.event;

return [{
  json: {
    categories,
    ...event
  }
}];

// Parse LLM output (strip markdown if present)
let text = $input.item.json.text.trim();

// Remove markdown code block markers if present
text = text.replace(/```json\n?/g, '').replace(/```\n?/g, '');

const contextData = $('Prepare LLM Context').first().json;

try {
  const parsed = JSON.parse(text);
  return [{
    json: {
      category_name: parsed.category,
      confidence: parsed.confidence,
      all_scores: parsed.all_scores,
      description: contextData.clean_text,  // Use original clean_text as description
      ...contextData
    }
  }];
} catch (error) {
  // Fallback: if parsing fails, default to "other" category
  return [{
    json: {
      category_name: "other",
      confidence: 0.5,
      all_scores: {},
      description: contextData.clean_text,
      parse_error: error.message,
      ...contextData
    }
  }];
}

