const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// Get current timestamp
const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
const backupDir = `/tmp/n8n-backup-${timestamp}`;

// Ensure backup directory exists
execSync(`mkdir -p ${backupDir}`);

// Files to backup
const files = [
  'n8n-workflows',
  'n8n-workflows-dev',
  'scripts',
  '.env',
  'DEPLOYMENT.md',
  'README.md'
];

const results = [];

for (const file of files) {
  try {
    if (fs.existsSync(file)) {
      const dest = path.join(backupDir, file);
      execSync(`cp -r ${file} ${dest}`);
      results.push({ file, status: 'success', path: dest });
    } else {
      results.push({ file, status: 'not_found' });
    }
  } catch (error) {
    results.push({ file, status: 'error', error: error.message });
  }
}

// Create backup info file
const backupInfo = {
  timestamp,
  backupDir,
  results,
  createdAt: new Date().toISOString()
};

fs.writeFileSync(path.join(backupDir, 'backup-info.json'), JSON.stringify(backupInfo, null, 2));

return [{
  json: {
    ctx: {
      event: {
        event_id: $json.id || 'backup-' + timestamp,
        event_type: 'backup_workflow',
        clean_text: 'Backup workflow execution',
        trace_chain: [timestamp],
        author_login: 'system',
        timestamp: new Date().toISOString()
      },
      backup: {
        results,
        backupDir,
        timestamp
      }
    }
  }
}];

const backupDir = $json.ctx.backup.backupDir;
const results = $json.ctx.backup.results;

const successCount = results.filter(r => r.status === 'success').length;
const errorCount = results.filter(r => r.status === 'error').length;
const notFoundCount = results.filter(r => r.status === 'not_found').length;

const message = `Backup completed successfully!\n\nðŸ“ Backup Directory: ${backupDir}\nâœ… Successfully backed up: ${successCount} items\nâŒ Failed to backup: ${errorCount} items\nâš ï¸  Not found: ${notFoundCount} items\n\nDetails:\n${results.map(r => `  - ${r.file}: ${r.status}${r.error ? ' (' + r.error + ')' : ''}`).join('\n')}`;

return [{
  json: {
    ctx: {
      ...$json.ctx,
      message: message
    }
  }
}];

const message = $json.ctx.message;

// Send success message to Discord
const webhookUrl = process.env.DISCORD_WEBHOOK_URL;
if (webhookUrl) {
  const payload = {
    content: message,
    username: 'Kairon Backup Bot',
    avatar_url: 'https://cdn.discordapp.com/emojis/123456789012345678.png' // Kairon logo
  };

  await $node['HTTP Request'].run({
    method: 'POST',
    url: webhookUrl,
    body: JSON.stringify(payload),
    headers: {
      'Content-Type': 'application/json'
    }
  });
}

return [{
  json: {
    ctx: {
      ...$json.ctx,
      discord_sent: true
    }
  }
}];
