// Merge DB results into ctx pattern
// All inputs come via Merge node in append mode
const allInputs = $input.all();

// Get thread messages (items with role and text properties)
const messages = allInputs
  .filter(item => item.json.role !== undefined && item.json.text !== undefined)
  .toReversed();

// Format thread history for LLM
const threadHistory = messages.map(msg => {
  return `${msg.json.role.toUpperCase()}: ${msg.json.text}`;
}).join('\n');

// Get ctx from trigger (already initialized by Route_Message)
const ctx = $('Execute Workflow Trigger').first().json.ctx;

return [{
  json: {
    ctx: {
      ...ctx,
      db: {
        thread_id: ctx.event.thread_id,
        thread_history: threadHistory,
        message_count: messages.length
      }
    }
  }
}];

// Parse LLM output in pipe format: "type|text"
let llmOutput = $input.first().json.text.trim();
const ctx = $('Prepare Context').first().json.ctx;

try {
  const lines = llmOutput.split('\n').filter(line => line.includes('|'));
  const extractions = [];
  let displayOrder = 1;

  lines.forEach(line => {
    const parts = line.split('|');
    if (parts.length < 2) return;

    const itemType = parts[0].trim();
    const text = parts.slice(1).join('|').trim(); // Handle pipes in text

    // Validate item_type
    if (!['reflection', 'fact', 'todo'].includes(itemType)) {
      console.error(`Invalid item_type: ${itemType}`);
      return;
    }

    extractions.push({
      thread_id: ctx.db.thread_id,
      item_type: itemType,
      text: text,
      display_order: displayOrder++
    });
  });

  // Return ctx with llm namespace added
  return [{
    json: {
      ctx: {
        ...ctx,
        llm: {
          raw_output: llmOutput,
          extractions: extractions,
          has_extractions: extractions.length > 0
        }
      }
    }
  }];
} catch (error) {
  // On error, return empty extractions
  return [{
    json: {
      ctx: {
        ...ctx,
        llm: {
          raw_output: llmOutput,
          extractions: [],
          has_extractions: false,
          parse_error: error.message
        }
      }
    }
  }];
}

// Build SQL query to insert extractions as projections
const ctx = $('Has Extractions?').first().json.ctx;
const traceResult = $('Write Thread Extraction Trace').first().json;
const autoConfirmResult = $('Get Auto Confirm Setting').first().json;
const extractions = ctx.llm.extractions;
const extractionTraceId = traceResult.trace_id;
const autoConfirm = autoConfirmResult?.value === 'true';

if (!extractions || extractions.length === 0) {
  return [];
}

// Format trace_chain for PostgreSQL uuid[] type
const traceChain = ctx.event.trace_chain || [];
const updatedTraceChain = [...traceChain, extractionTraceId];
const traceChainPg = '{' + updatedTraceChain.join(',') + '}';

// 7 parameters per row: event_id, trace_id, trace_chain, projection_type, data (jsonb), status, timezone
const values = extractions.map((ext, idx) => {
  const offset = idx * 7;
  return `($${offset + 1}::uuid, $${offset + 2}::uuid, $${offset + 3}::uuid[], $${offset + 4}, $${offset + 5}::jsonb, $${offset + 6}, $${offset + 7})`;
}).join(', ');

const query = `
INSERT INTO projections (
  event_id,
  trace_id,
  trace_chain,
  projection_type,
  data,
  status,
  timezone
)
VALUES ${values}
RETURNING *;
`;

// The params array matches the $1, $2, $3, $4, $5, $6, $7... sequence (7 per extraction)
const params = [];
extractions.forEach(ext => {
  params.push(ctx.event.event_id);  // event_id
  params.push(extractionTraceId);    // trace_id
  params.push(traceChainPg);         // trace_chain
  params.push('thread_extraction');  // projection_type
  params.push(JSON.stringify({       // data as JSONB
    thread_id: ext.thread_id,
    item_type: ext.item_type,
    text: ext.text,
    display_order: ext.display_order
  }));
  params.push(autoConfirm ? 'confirmed' : 'pending');            // status
  params.push(ctx.event.timezone || null);  // timezone
});

return [{
  json: {
    ctx: {
      ...ctx,
      db: {
        ...ctx.db,
        trace_id: extractionTraceId
      }
    },
    sql_query: query,
    sql_params: params
  }
}];

