    emoji: '‚ùå'
  },
  {
    url: `https://discord.com/api/v10/channels/${ctx.event.channel_id}/messages/${summaryMessageId}/reactions/%F0%9F%97%91%EF%B8%8F/@me`,
    emoji: 'üóëÔ∏è'
  }
);

return reactionUrls.map(r => ({
  json: {
    ctx: ctx,
    summary_message_id: summaryMessageId,
    reaction_url: r.url,
    emoji: r.emoji
  }
}));

// Prepare trace data for thread extraction (using ctx pattern)
const ctx = $('Set Inference Start').first().json.ctx;
const inferenceStart = ctx.timing?.inference_start || Date.now();
const durationMs = Date.now() - inferenceStart;
const llmOutput = $('Extract Items LLM').first().json.text;
const parsedCtx = $input.first().json.ctx;

// Build filled prompt
const promptText = `You are analyzing a conversation thread to extract key insights and actionable items.

## Thread History
${ctx.db.thread_history}

## Task
Extract and classify the most important items from the thread...`;

// Format trace_chain for PostgreSQL uuid[] type
const traceChain = ctx.event.trace_chain || [];
const traceChainPg = '{' + traceChain.join(',') + '}';

return [{
  json: {
    ctx: parsedCtx,
    // Trace data for query
    input_thread_history: ctx.db.thread_history,
    input_thread_id: ctx.db.thread_id,
    prompt_text: promptText,
    completion_text: llmOutput,
    result_extraction_count: parsedCtx.llm.extractions?.length || 0,
    result_has_extractions: parsedCtx.llm.has_extractions,
    duration_ms: durationMs,
    event_id: ctx.event.event_id,
    trace_chain_pg: traceChainPg
  }
}];
