// Handle ping command
const event = $('When Executed by Another Workflow').first().json.event;

return {
  response: 'üèì pong',
  ...event
};

// Format get command response (validation already done)
const event = $('When Executed by Another Workflow').first().json.event;
const args = $('Parse Command and Args').item.json.args;
const key = args[0];
const result = $input.first()?.json;

// Check if query returned data
if (!result || !result.value) {
  return {
    response: `‚ùå Config key \`${key}\` not found.\n\nUse \`::set ${key} <value>\` to set it first.`,
    ...event
  };
}

return {
  response: `**${key}:** ${result.value}`,
  ...event
};

// Format set command response (validation already done)
const result = $input.first()?.json;
const event = $('When Executed by Another Workflow').first().json.event;

// Check if database operation succeeded
if (!result || !result.key) {
  return {
    response: `‚ùå Failed to set config value. Please try again.`,
    ...event
  };
}

return {
  response: `‚úÖ Set **${result.key}** = ${result.value}`,
  ...event
};

// Format stats response
const stats = $input.item.json;
const event = $('When Executed by Another Workflow').first().json.event;

const lastActivity = stats.last_activity 
  ? new Date(stats.last_activity).toLocaleString()
  : 'Never';

return {
  response: `üìä **Statistics**\n\n` +
    `Activities today: ${stats.activities_today}\n` +
    `Notes this week: ${stats.notes_this_week}\n` +
    `Total events: ${stats.total_events}\n` +
    `Last activity: ${lastActivity}`,
  ...event
};

// Format recent items response with short IDs
const items = $input.all();
const event = $('When Executed by Another Workflow').first().json.event;
const args = $('Parse Command and Args').item.json.args;
const itemType = args[0] || 'activities';

if (items.length === 0) {
  return { 
    response: `üì≠ No recent ${itemType} found`,
    ...event
  };
}

// Determine icon and title based on type
const firstItem = items[0].json;
const isActivity = firstItem.type === 'activity';
const icon = isActivity ? 'üìä' : 'üìù';
const title = isActivity ? 'Activities' : 'Notes';

let response = `${icon} **Recent ${title} (${items.length})**\n\n`;

items.forEach((item, i) => {
  const data = item.json;
  const date = new Date(data.timestamp);
  const dateStr = date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  const timeStr = date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: false });

  const text = data.text.length > 60 
    ? data.text.substring(0, 60) + '...' 
    : data.text;

  response += `${i + 1}. \`[${data.short_id}]\` ${dateStr} ${timeStr} - **${data.category_name}** - ${text}\n`;
});

response += `\nüí° Use \`::delete activity <short-id>\` or \`::delete note <short-id>\` to delete items`;

return { response, ...event };

// Format status response
const status = $input.item.json;
const event = $('When Executed by Another Workflow').first().json.event;

const sleepIcon = status.sleeping ? 'üò¥' : 'üëÅÔ∏è';
const sleepStatus = status.sleeping ? 'Sleeping' : 'Awake';
const lastActivity = status.last_observation_at
  ? new Date(status.last_observation_at).toLocaleString()
  : 'Never';
const minutesAgo = Math.round(status.minutes_since_activity || 0);

return {
  response: `${sleepIcon} **Status**\n\n` +
    `State: ${sleepStatus}\n` +
    `Last activity: ${lastActivity}\n` +
    `Time since: ${minutesAgo} minutes ago`,
  ...event
};

// Unknown command handler
const command = $('Parse Command and Args').item.json.command;
const event = $('When Executed by Another Workflow').first().json.event;

return {
  response: `‚ùå Unknown command: \`${command}\`\n\nUse \`::help\` to see available commands.`,
  ...event
};

// Format delete response (validation already done)
const deleted = $input.all();
const event = $('When Executed by Another Workflow').first().json.event;
const args = $('Parse Command and Args').item.json.args;
const itemType = args[0];

// Check if any items were actually deleted
if (deleted.length === 0) {
  const shortIds = args.slice(1).filter(id => id).join(', ');
  return {
    response: `‚ùå No ${itemType === 'activity' ? 'activities' : 'notes'} found with ID(s): ${shortIds}\n\nUse \`::recent ${itemType === 'activity' ? 'activities' : 'notes'}\` to see valid IDs.`,
    ...event
  };
}

if (deleted.length === 1) {
  const item = deleted[0].json;
  const text = item.text.length > 60 ? item.text.substring(0, 60) + '...' : item.text;
  return {
    response: `‚úÖ Deleted ${item.type}: "${text}"`,
    ...event
  };
}

const typeLabel = deleted[0].json.type === 'activity' ? 'activities' : 'notes';
return {
  response: `‚úÖ Deleted ${deleted.length} ${typeLabel}`,
  ...event
};

// Validate GET command
const event = $('When Executed by Another Workflow').first().json.event;
const args = $('Parse Command and Args').item?.json?.args || [];
const key = args[0];

// Validation: check if key provided
if (!key || key.trim() === '') {
  return {
    valid: false,
    error_message: `‚ùå Missing config key. Syntax: \`::get <key>\`\n\nAvailable keys: north_star, summary_time\nUse \`::help\` for more info.`,
    ...event
  };
}

// Valid
return {
  valid: true,
  ...event
};

// Validate SET command
const event = $('When Executed by Another Workflow').first().json.event;
const args = $('Parse Command and Args').item?.json?.args || [];

// Validation: need at least 2 args (key and value)
if (!args || args.length < 2) {
  return {
    valid: false,
    error_message: `‚ùå Missing arguments. Syntax: \`::set <key> <value>\`\n\nExample: \`::set north_star Be present and intentional\`\n\nAvailable keys: north_star, summary_time`,
    ...event
  };
}

const key = args[0];
if (!key || key.trim() === '') {
  return {
    valid: false,
    error_message: `‚ùå Invalid config key (empty string).\n\nExample: \`::set north_star <your principle>\``,
    ...event
  };
}

// Valid
return {
  valid: true,
  ...event
};

// Validate DELETE command
const event = $('When Executed by Another Workflow').first().json.event;
const args = $('Parse Command and Args').item?.json?.args || [];
const itemType = args[0];

// Validation: need item type (activity/note)
if (!itemType || !['activity', 'note', 'activities', 'notes'].includes(itemType)) {
  return {
    valid: false,
    error_message: `‚ùå Invalid item type. Syntax: \`::delete activity <short-id>\` or \`::delete note <short-id>\`\n\nUse \`::recent activities\` or \`::recent notes\` to see valid IDs.`,
    ...event
  };
}

// Validation: need at least one short ID
if (args.length < 2 || !args[1] || args[1].trim() === '') {
  return {
    valid: false,
    error_message: `‚ùå Missing short ID. Syntax: \`::delete ${itemType} <short-id>\`\n\nExample: \`::delete ${itemType} a1b2c3d4\`\nUse \`::recent ${itemType === 'activity' || itemType === 'activities' ? 'activities' : 'notes'}\` to see valid IDs.`,
    ...event
  };
}

// Validation: check short ID format (at least 4 chars)
const shortIds = args.slice(1).filter(id => id && id.trim() !== '');
const invalidIds = shortIds.filter(id => id.length < 4);
if (invalidIds.length > 0) {
  return {
    valid: false,
    error_message: `‚ùå Invalid short ID format: ${invalidIds.join(', ')}\n\nShort IDs must be at least 4 characters. Use \`::recent ${itemType === 'activity' || itemType === 'activities' ? 'activities' : 'notes'}\` to see valid IDs.`,
    ...event
  };
}

// Valid
return {
  valid: true,
  ...event
};
