// Get event and conversation from previous nodes
const event = $('Execute Workflow Trigger').first().json.event;
const conversation = $('Get Conversation').first().json;

// Get all merged inputs
const allInputs = $input.all();

// Extract conversation history
const historyItems = allInputs.filter(item => item.json.role !== undefined && item.json.text !== undefined);
const history = historyItems.reverse().map(item => ({
  role: item.json.role,
  text: item.json.text
}));

// Extract north star
let northStar = 'Not set';
const northStarItem = allInputs.find(item => item.json.value !== undefined && item.json.key === undefined);
if (northStarItem) {
  northStar = northStarItem.json.value;
}

// Extract activities
const activities = allInputs
  .filter(item => item.json.category && item.json.description)
  .map(item => ({
    timestamp: item.json.timestamp,
    category: item.json.category,
    description: item.json.description
  }));

// Extract notes
const notes = allInputs
  .filter(item => item.json.category && item.json.text)
  .map(item => ({
    timestamp: item.json.timestamp,
    category: item.json.category,
    text: item.json.text
  }));

return [{
  json: {
    ...event,
    conversation_id: conversation.id,
    north_star: northStar,
    history: history,
    activities: activities,
    notes: notes
  }
}];

// Prepare trace data after LLM inference (Option E schema)
const context = $('Set Inference Start').first().json.context;
const event = $('Execute Workflow Trigger').first().json.event;
const inferenceStart = $('Set Inference Start').first().json.inference_start;
const durationMs = Date.now() - inferenceStart;
const response = $input.first().json.text;

// Build filled prompt (reconstructed from template)
const promptText = `You are an AI life coach helping the user reflect, plan, and think deeply.

## Context Available

**User's Guiding Principle:** ${context.north_star}

Keep this in mind when helping them, but don't mention it unless directly relevant to the conversation.

**Recent Activities (Last 20)**
${context.activities.map(act => `${act.timestamp} - ${act.description}`).join('\n')}

**Recent Notes (Last 10)**
${context.notes.map(n => `[${n.category}] ${n.text}`).join('\n')}

## Instructions
...

## Conversation History
${context.history.map(msg => `${msg.role.toUpperCase()}: ${msg.text}`).join('\n\n')}

## Current Message
The user just said: "${context.clean_text}"

Respond to their message now.`;

return [{
  json: {
    // Option E schema for trace
    input_text: context.clean_text,
    input_conversation_id: context.conversation_id,
    input_history_length: context.history.length,
    prompt_text: promptText,
    completion_text: response,
    result_response_length: response.length,
    result_referenced_activities: context.activities.length,
    result_referenced_notes: context.notes.length,
    duration_ms: durationMs,
    event_id: event.event_id,
    trace_chain: event.trace_chain || [],
    ...context  // Pass through all context fields
  }
}];
