// Prepare batch database queries for Execute_Queries sub-workflow
const ctx = $json.ctx;

const db_queries = [
  {
    key: 'history',
    sql: `SELECT timestamp, role, text FROM thread_history 
          WHERE thread_id = $1 AND NOT (role = 'user' AND text = $2) 
          ORDER BY timestamp ASC LIMIT 20`,
    params: [ctx.event.thread_id, ctx.event.clean_text]
  },
  {
    key: 'north_star',
    sql: `SELECT value FROM config WHERE key = 'north_star'`
  },
  {
    key: 'activities',
    sql: `SELECT 
            (data->>'timestamp')::timestamptz as timestamp,
            data->>'category' as category,
            data->>'description' as description
          FROM projections
          WHERE projection_type = 'activity'
            AND status IN ('auto_confirmed', 'confirmed')
          ORDER BY (data->>'timestamp')::timestamptz DESC
          LIMIT 20`
  },
  {
    key: 'notes',
    sql: `SELECT 
            (data->>'timestamp')::timestamptz as timestamp,
            data->>'category' as category,
            data->>'text' as text
          FROM projections
          WHERE projection_type = 'note'
            AND status IN ('auto_confirmed', 'confirmed')
          ORDER BY (data->>'timestamp')::timestamptz DESC
          LIMIT 10`
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    }
  }
}];

// Build LLM context from Execute_Queries results
const ctx = $json.ctx;
const db = ctx.db || {};

// Extract and sort conversation history
const history = (db.history?.rows || [])
  .sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp))
  .map(item => ({
    role: item.role,
    text: item.text
  }));

// Extract north star
const northStar = db.north_star?.row?.value || 'Not set';

// Extract activities
const activities = (db.activities?.rows || []).map(item => ({
  timestamp: item.timestamp,
  category: item.category,
  description: item.description
}));

// Extract notes  
const notes = (db.notes?.rows || []).map(item => ({
  timestamp: item.timestamp,
  category: item.category,
  text: item.text
}));

return [{
  json: {
    ctx: {
      ...ctx,
      llm: {
        ...ctx.llm,
        north_star: northStar,
        inference_start: Date.now(),
        history: history,
        activities: activities,
        notes: notes
      }
    }
  }
}];

// Prepare write queries for Execute_Queries (trace + projection)
const ctx = $json.ctx || {};
const response = ctx.llm?.completion_text || '';
const inferenceStart = ctx.llm?.inference_start || Date.now();
const durationMs = Date.now() - inferenceStart;

// Build filled prompt (reconstructed from template)
const promptText = `You are an AI life coach helping the user reflect, plan, and think deeply.

## Context Available

**User's Guiding Principle:** ${ctx.llm?.north_star || '(not set)'}

**Recent Activities (Last 20)**
${(ctx.llm?.activities || []).map(act => `${act.timestamp} - ${act.description}`).join('\n')}

**Recent Notes (Last 10)**
${(ctx.llm?.notes || []).map(n => `[${n.category}] ${n.text}`).join('\n')}

## Instructions
...

## Conversation History
${(ctx.llm?.history || []).map(msg => `${msg.role.toUpperCase()}: ${msg.text}`).join('\n\n')}

## Current Message
The user just said: "${ctx.event?.clean_text || ''}"

Respond to their message now.`;

// Format trace_chain for PostgreSQL uuid[] type
const traceChain = ctx.event?.trace_chain || [];

// Build write queries with chaining
const db_queries = [
  {
    key: 'trace',
    sql: `INSERT INTO traces (event_id, step_name, data, trace_chain)
          VALUES (
            $1::uuid,
            'thread_response',
            jsonb_build_object(
              'input', jsonb_build_object(
                'text', $2,
                'thread_id', $3::text,
                'history_length', $4::integer
              ),
              'prompt', $5,
              'completion', $6,
              'result', jsonb_build_object(
                'response_length', $7::integer,
                'referenced_activities', $8::integer,
                'referenced_notes', $9::integer
              ),
              'model', 'xiaomi/mimo-v2-flash:free',
              'duration_ms', $10::integer
            ),
            $11::uuid[]
          )
          RETURNING id, $11::uuid[] || id AS updated_trace_chain`,
    params: [
      ctx.event?.event_id,
      ctx.event?.clean_text || '',
      ctx.event?.thread_id,
      (ctx.llm?.history || []).length,
      promptText,
      response,
      response.length,
      (ctx.llm?.activities || []).length,
      (ctx.llm?.notes || []).length,
      durationMs,
      traceChain
    ]
  },
  {
    key: 'projection',
    sql: `INSERT INTO projections (
            event_id,
            trace_id,
            trace_chain,
            projection_type,
            status,
            data,
            timezone
          )
          VALUES (
            $1::uuid,
            $2::uuid,
            $3::uuid[],
            'thread_response',
            'auto_confirmed',
            jsonb_build_object(
              'thread_id', $4,
              'response_text', $5,
              'role', 'assistant',
              'timestamp', $6::timestamptz
            ),
            $7
          )
          RETURNING id`,
    params: [
      ctx.event?.event_id,
      '$results.trace.row.id',
      '$results.trace.row.updated_trace_chain',
      ctx.event?.thread_id,
      response,
      ctx.event?.timestamp,
      ctx.event?.timezone
    ]
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    }
  }
}];
