// Check if it's time to run the summary
const now = new Date();
const currentHour = now.getHours();
const currentMinute = now.getMinutes();

// Get configured time (default to 20:00 if not set)
const configResult = $input.all();
let targetHour = 20;

if (configResult.length > 0 && configResult[0].json.value) {
  const configTime = configResult[0].json.value; // Format: "HH:MM" or "HH"
  if (configTime.includes(':')) {
    targetHour = parseInt(configTime.split(':')[0]);
  } else {
    targetHour = parseInt(configTime);
  }
}

// Check if current time matches target hour (run between :00 and :15 to allow for cron variance)
const shouldRun = currentHour === targetHour && currentMinute < 30;

if (!shouldRun) {
  // Stop execution - not time yet
  return [];
}

// Continue execution
return [{ json: { should_run: true, target_hour: targetHour } }];

// Safely merge all data for LLM summarization

// Get north star (handle empty result)
const northStarItems = $input.all().filter(item => item.json.value !== undefined);
const northStar = northStarItems.length > 0 ? northStarItems[0].json.value : 'Not set';

// Get activities (handle empty result)
const activityItems = $input.all().filter(item => item.json.description !== undefined);

// Get notes (handle empty result)
const noteItems = $input.all().filter(item => item.json.text !== undefined);

// Get breakdown (handle empty result)
const breakdownItems = $input.all().filter(item => item.json.category !== undefined && item.json.count !== undefined && !item.json.description && !item.json.text);

// Format activities list
let activitiesList = '';
if (activityItems.length === 0) {
  activitiesList = '(No activities logged today)';
} else {
  activityItems.forEach(item => {
    const time = new Date(item.json.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    activitiesList += `- ${time} [${item.json.category}] ${item.json.description}\n`;
  });
}

// Format notes list
let notesList = '';
if (noteItems.length === 0) {
  notesList = '(No notes captured today)';
} else {
  noteItems.forEach(item => {
    const time = new Date(item.json.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    const title = item.json.title ? `**${item.json.title}**: ` : '';
    notesList += `- ${time} [${item.json.category}] ${title}${item.json.text}\n`;
  });
}

// Format category breakdown
let breakdownText = '';
if (breakdownItems.length === 0) {
  breakdownText = '(No category data)';
} else {
  breakdownItems.forEach(item => {
    breakdownText += `- ${item.json.category}: ${item.json.count} activities\n`;
  });
}

return [{
  json: {
    north_star: northStar,
    activities_list: activitiesList,
    notes_list: notesList,
    category_breakdown: breakdownText,
    activity_count: activityItems.length,
    note_count: noteItems.length
  }
}];
