// Handle ping command
const ctx = $('When Executed by Another Workflow').first().json.ctx;

return {
  ctx: {
    ...ctx,
    response: {
      content: 'üèì pong'
    }
  }
};

// Format get command response - merge DB result into ctx
const ctx = $('If Valid Get').first().json.ctx;
const result = $input.item?.json;

// Check if query returned data
if (!result || !result.key || !result.value) {
  const key = ctx.validation.normalized_key;
  return {
    ctx: {
      ...ctx,
      response: {
        content: `‚ùå Config key \`${key}\` not found.\n\nUse \`::set ${key} <value>\` to set it first.\n\nAvailable keys: north_star, summary_time`
      }
    }
  };
}

return {
  ctx: {
    ...ctx,
    db: { config_key: result.key, config_value: result.value },
    response: {
      content: `**${result.key}:** ${result.value}`
    }
  }
};

// Format set command response - merge DB result into ctx
const ctx = $('If Valid Set').first().json.ctx;
const result = $input.item?.json;

// Check if database operation succeeded
if (!result || !result.key) {
  return {
    ctx: {
      ...ctx,
      response: {
        content: `‚ùå Failed to set config value. Please try again.`
      }
    }
  };
}

return {
  ctx: {
    ...ctx,
    db: { config_key: result.key, config_value: result.value },
    response: {
      content: `‚úÖ Set **${result.key}** = ${result.value}`
    }
  }
};

// Format stats response - merge DB result into ctx
const ctx = $('When Executed by Another Workflow').first().json.ctx;
const stats = $input.item?.json;

if (!stats) {
  return {
    ctx: {
      ...ctx,
      response: {
        content: `‚ùå Unable to retrieve statistics. Please try again.`
      }
    }
  };
}

const lastActivity = stats.last_activity 
  ? new Date(stats.last_activity).toLocaleString()
  : 'Never';

return {
  ctx: {
    ...ctx,
    db: { stats },
    response: {
      content: `üìä **Statistics**\n\n` +
        `Activities today: ${stats.activities_today || 0}\n` +
        `Notes this week: ${stats.notes_this_week || 0}\n` +
        `Total events: ${stats.total_events || 0}\n` +
        `Last activity: ${lastActivity}`
    }
  }
};

// Format recent items response (numbered list for easy deletion)
const ctx = $('If Valid Recent').first().json.ctx;
const items = $input.all();
const itemType = ctx.validation.normalized_type;

if (!items || items.length === 0) {
  return {
    ctx: {
      ...ctx,
      response: {
        content: `üì≠ No recent ${itemType} found`
      }
    }
  };
}

// Determine icon and title based on type
const firstItem = items[0].json;
const isActivity = firstItem.type === 'activity';
const icon = isActivity ? 'üìä' : 'üìù';
const title = isActivity ? 'Activities' : 'Notes';

let responseText = `${icon} **Recent ${title} (${items.length})**\n\n`;

items.forEach((item, i) => {
  const data = item.json;
  // Use Luxon (n8n built-in) to convert UTC to local timezone
  const dt = DateTime.fromISO(data.timestamp, { zone: 'utc' }).setZone('America/Los_Angeles');
  const dateStr = dt.toFormat('MMM d');  // "Dec 17"
  const timeStr = dt.toFormat('HH:mm');   // "19:24" (24-hour)

  const text = data.text.length > 60 
    ? data.text.substring(0, 60) + '...' 
    : data.text;

  // Build Discord message link if we have all the IDs
  let link = '';
  if (data.guild_id && data.channel_id && data.message_id) {
    const url = `https://discord.com/channels/${data.guild_id}/${data.channel_id}/${data.message_id}`;
    link = ` [‚Üó](${url})`;
  }

  responseText += `${i + 1}. ${dateStr} ${timeStr} - **${data.category}** - ${text}${link}\n`;
});

responseText += `\nüí° Use \`::delete ${isActivity ? 'activity' : 'note'} <number>\` to delete items\nExample: \`::delete ${isActivity ? 'activity' : 'note'} 1\` or \`::delete ${isActivity ? 'activity' : 'note'} 1 3 5\``;

return {
  ctx: {
    ...ctx,
    db: { recent_items: items.map(i => i.json) },
    response: {
      content: responseText
    }
  }
};

// Format status response - merge DB result into ctx
const ctx = $('When Executed by Another Workflow').first().json.ctx;
const status = $input.item?.json;

if (!status) {
  return {
    ctx: {
      ...ctx,
      response: {
        content: `‚ùå Unable to retrieve status. Please try again.`
      }
    }
  };
}

const isSleeping = status.last_activity_category === 'sleep';
const sleepIcon = isSleeping ? 'üò¥' : 'üëÅÔ∏è';
const sleepStatus = isSleeping ? 'Sleeping' : 'Awake';
const lastActivity = status.last_observation_at
  ? new Date(status.last_observation_at).toLocaleString()
  : 'Never';
const minutesAgo = Math.round(status.minutes_since_activity || 0);
const lastCategory = status.last_activity_category || 'unknown';

return {
  ctx: {
    ...ctx,
    db: { user_status: status },
    response: {
      content: `${sleepIcon} **Status**\n\n` +
        `State: ${sleepStatus}\n` +
        `Last activity: ${lastActivity}\n` +
        `Last category: ${lastCategory}\n` +
        `Time since: ${minutesAgo} minutes ago`
    }
  }
};

// Unknown command handler
const ctx = $('When Executed by Another Workflow').first().json.ctx;
const command = ctx.command.name;

return {
  ctx: {
    ...ctx,
    response: {
      content: `‚ùå Unknown command: \`${command}\`\n\nUse \`::help\` to see available commands.`
    }
  }
};

// Format void response (index-based soft-delete)
const ctx = $('If Valid Delete').first().json.ctx;
const voided = $input.all();
const itemType = ctx.validation.normalized_item_type || 'activity';
const indices = ctx.validation.indices || [];

// Check if any items were actually voided
if (!voided || voided.length === 0) {
  const indexStr = indices.join(', ');
  return {
    ctx: {
      ...ctx,
      response: {
        content: `‚ùå No ${itemType}s found at position(s): ${indexStr}\n\nUse \`::recent ${itemType}s\` to see the current numbered list.`
      }
    }
  };
}

if (voided.length === 1) {
  const item = voided[0].json;
  const text = item.text.length > 60 ? item.text.substring(0, 60) + '...' : item.text;
  return {
    ctx: {
      ...ctx,
      db: { voided_items: [item] },
      response: {
        content: `‚úÖ Removed ${item.type} #${indices[0]}: "${text}"`
      }
    }
  };
}

const typeLabel = voided[0].json.type === 'activity' ? 'activities' : 'notes';
return {
  ctx: {
    ...ctx,
    db: { voided_items: voided.map(d => d.json) },
    response: {
      content: `‚úÖ Removed ${voided.length} ${typeLabel} at positions: ${indices.join(', ')}`
    }
  }
};

// Validate GET command
const ctx = $('When Executed by Another Workflow').first().json.ctx;
const args = $('Parse Command and Args').item?.json?.ctx?.command?.args || [];

// Join all args into a single key (support "north star" -> "north_star")
const key = args.join('_');

// Validation: check if key provided
if (!key || key.trim() === '') {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå Missing config key. Syntax: \`::get <key>\`\n\nAvailable keys: north_star, summary_time\nUse \`::help\` for more info.`
      }
    }
  };
}

// Valid - pass the normalized key
return {
  ctx: {
    ...ctx,
    validation: {
      valid: true,
      normalized_key: key
    }
  }
};

// Validate SET command
const ctx = $('When Executed by Another Workflow').first().json.ctx;
const args = $('Parse Command and Args').item?.json?.ctx?.command?.args || [];

// Validation: need at least 2 args (key and value)
if (!args || args.length < 2) {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå Missing arguments. Syntax: \`::set <key> <value>\`\n\nExample: \`::set north_star Be present and intentional\`\n\nAvailable keys: north_star, summary_time`
      }
    }
  };
}

// Normalize key: convert spaces to underscores (north_star or summary_time)
const key = args[0];
if (!key || key.trim() === '') {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå Invalid config key (empty string).\n\nExample: \`::set north_star <your principle>\``
      }
    }
  };
}

// Join remaining args as value
const value = args.slice(1).join(' ');

// Valid - pass normalized key and value
return {
  ctx: {
    ...ctx,
    validation: {
      valid: true,
      normalized_key: key,
      normalized_value: value
    }
  }
};

// Validate DELETE command (uses indices, not short IDs)
const ctx = $('When Executed by Another Workflow').first().json.ctx;
const args = $('Parse Command and Args').item?.json?.ctx?.command?.args || [];
const itemType = args[0];

// Validation: need item type (activity/note)
if (!itemType || !['activity', 'note', 'activities', 'notes'].includes(itemType)) {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå Invalid item type. Syntax: \`::delete activity <index>\` or \`::delete note <index>\`\n\nUse \`::recent activities\` or \`::recent notes\` to see numbered list.`
      }
    }
  };
}

// Validation: need at least one index
if (args.length < 2 || !args[1]) {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå Missing index. Syntax: \`::delete ${itemType} <index>\`\n\nExample: \`::delete ${itemType} 1\` (deletes first item)\nUse \`::recent ${itemType === 'activity' || itemType === 'activities' ? 'activities' : 'notes'}\` to see numbered list.`
      }
    }
  };
}

// Validation: check indices are valid numbers (1-100)
const indices = args.slice(1).filter(arg => arg !== null && arg !== undefined);
const invalidIndices = [];
const validIndices = [];

for (const idx of indices) {
  const num = parseInt(idx);
  if (isNaN(num) || num < 1 || num > 100) {
    invalidIndices.push(idx);
  } else {
    validIndices.push(num);
  }
}

if (invalidIndices.length > 0) {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå Invalid index: ${invalidIndices.join(', ')}\n\nIndices must be numbers between 1 and 100.\nUse \`::recent ${itemType === 'activity' || itemType === 'activities' ? 'activities' : 'notes'}\` to see numbered list.`
      }
    }
  };
}

if (validIndices.length === 0) {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå No valid indices provided.\n\nExample: \`::delete ${itemType} 1 2 3\``
      }
    }
  };
}

// Normalize type to singular form
const normalizedItemType = itemType === 'activities' ? 'activity' : (itemType === 'notes' ? 'note' : itemType);

// Valid - pass normalized values and indices
return {
  ctx: {
    ...ctx,
    validation: {
      valid: true,
      normalized_item_type: normalizedItemType,
      indices: validIndices
    }
  }
};

// Validate RECENT command
const ctx = $('When Executed by Another Workflow').first().json.ctx;
const args = $('Parse Command and Args').item?.json?.ctx?.command?.args || [];

// Default values
const itemType = args[0] || 'activities';
const limit = args[1] ? parseInt(args[1]) : 10;

// Validation: check item type is valid
const validTypes = ['activities', 'activity', 'notes', 'note', ''];
if (!validTypes.includes(itemType)) {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå Invalid item type: \`${itemType}\`\n\nValid types: activities, notes\nSyntax: \`::recent [activities|notes] [limit]\`\n\nExample: \`::recent activities 20\``
      }
    }
  };
}

// Validation: check limit is a valid number (1-100)
if (isNaN(limit) || limit < 1 || limit > 100) {
  return {
    ctx: {
      ...ctx,
      validation: {
        valid: false,
        error_message: `‚ùå Invalid limit: \`${args[1]}\`\n\nLimit must be a number between 1 and 100.\nExample: \`::recent activities 20\``
      }
    }
  };
}

// Normalize type to plural form for query
const normalizedType = itemType === 'activity' ? 'activities' : (itemType === 'note' ? 'notes' : itemType);

// Valid - pass normalized values
return {
  ctx: {
    ...ctx,
    validation: {
      valid: true,
      normalized_type: normalizedType || 'activities',
      normalized_limit: limit
    }
  }
};
