// Validate input and initialize loop state
// Execute_Queries: Unified sub-workflow for all database operations
// - Executes queries sequentially (supports chaining via $results)
// - Preserves ctx throughout
// - Stores results in ctx.db[key] = { row, rows, count }

const ctx = $json.ctx;

// If no queries or empty array, return state that will skip the loop gracefully
if (!ctx?.db_queries || !Array.isArray(ctx.db_queries) || ctx.db_queries.length === 0) {
  return [{
    json: {
      original_ctx: ctx || {},
      queries: [],
      current_index: 0,
      results: {},
      has_more: false
    }
  }];
}

// Validate each query has required fields
for (let i = 0; i < ctx.db_queries.length; i++) {
  const q = ctx.db_queries[i];
  if (!q.key) {
    throw new Error(`Execute_Queries: Query at index ${i} missing 'key' field`);
  }
  if (!q.sql) {
    throw new Error(`Execute_Queries: Query '${q.key}' at index ${i} missing 'sql' field`);
  }
}

// Initialize loop state
return [{
  json: {
    original_ctx: ctx,
    queries: ctx.db_queries,
    current_index: 0,
    results: {},
    has_more: true
  }
}];

// Prepare current query for execution
// Supports $results.key.field references in params for chaining
// Also formats arrays as PostgreSQL array literals when needed
const state = $json;
const query = state.queries[state.current_index];

// Helper to format JS arrays as PostgreSQL array literals
// Handles special characters: commas, braces, quotes, backslashes
function formatPgArray(arr) {
  if (!Array.isArray(arr)) return arr;

  const formatted = arr.map(item => {
    if (Array.isArray(item)) {
      return formatPgArray(item); // Recursive for nested arrays
    }
    if (typeof item === 'string') {
      // Escape and quote strings with special characters
      if (item.includes(',') || item.includes('{') || item.includes('}') || 
          item.includes('"') || item.includes('\\') || item.includes(' ')) {
        return '"' + item.replace(/\\/g, '\\\\').replace(/"/g, '\\"') + '"';
      }
    }
    return item;
  });

  return '{' + formatted.join(',') + '}';
}

// Process params to resolve $results references
let params = query.params || [];

if (params.length > 0) {
  params = params.map(param => {
    if (typeof param === 'string' && param.startsWith('$results.')) {
      // Parse reference like "$results.trace.row.id"
      const path = param.substring('$results.'.length).split('.');
      let value = state.results;
      for (const key of path) {
        if (value && typeof value === 'object') {
          value = value[key];
        } else {
          value = undefined;
          break;
        }
      }
      if (value === undefined) {
        throw new Error(`Execute_Queries: Could not resolve reference ${param}`);
      }
      // Auto-format arrays for PostgreSQL
      return formatPgArray(value);
    }
    // Also format arrays that aren't references
    if (Array.isArray(param)) {
      return formatPgArray(param);
    }
    return param;
  });
}

return [{
  json: {
    ...state,
    current_query: query,
    sql: query.sql,
    params: params
  }
}];

// Collect query results and check if more queries remain
const items = $input.all();

// Find state from merge (has original_ctx)
let state = null;
const queryResults = [];

for (const item of items) {
  if (item.json.original_ctx) {
    state = item.json;
  } else {
    // This is a Postgres result row
    queryResults.push(item.json);
  }
}

if (!state) {
  throw new Error('Execute_Queries: Lost loop state');
}

// Store results for current query
const currentKey = state.current_query.key;
state.results[currentKey] = {
  row: queryResults[0] || null,  // First row (common for RETURNING or single-row SELECT)
  rows: queryResults,             // All rows
  count: queryResults.length      // Row count
};

// Move to next query
state.current_index++;

// Check if more queries remain
const hasMore = state.current_index < state.queries.length;

return [{
  json: {
    ...state,
    has_more: hasMore
  }
}];

// Restore ctx with all query results in ctx.db namespace
const state = $json;

// Remove db_queries since it was consumed
const { db_queries, ...restCtx } = state.original_ctx;

return [{
  json: {
    ctx: {
      ...restCtx,
      db: {
        ...(restCtx.db || {}),
        ...state.results
      }
    }
  }
}];
