// Extract trigger_reason from caller, prepare event data
const input = $json;
const triggerReason = input.trigger_reason || 'cron';
const today = $now.toFormat('yyyy-MM-dd');

return [{ json: { should_run: true, today: today, trigger_reason: triggerReason } }];

// Initialize ctx with event_id and prepare db_queries for Execute_Queries
const eventResult = $json;
const checkResult = $("Prepare Event Data").first().json;

const db_queries = [
  {
    key: 'north_star',
    sql: `SELECT value FROM config WHERE key = 'north_star'`
  },
  {
    key: 'activities',
    sql: `SELECT 
            (p.data->>'timestamp')::timestamptz as timestamp,
            p.data->>'category' as category,
            p.data->>'description' as description
          FROM projections p
          WHERE p.projection_type = 'activity'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          ORDER BY (p.data->>'timestamp')::timestamptz`
  },
  {
    key: 'notes',
    sql: `SELECT 
            (p.data->>'timestamp')::timestamptz as timestamp,
            p.data->>'category' as category,
            p.data->>'text' as text
          FROM projections p
          WHERE p.projection_type = 'note'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          ORDER BY (p.data->>'timestamp')::timestamptz`
  },
  {
    key: 'breakdown',
    sql: `SELECT 
            p.data->>'category' as category,
            COUNT(*) as count
          FROM projections p
          WHERE p.projection_type = 'activity'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          GROUP BY p.data->>'category'
          ORDER BY count DESC`
  }
];

return [{
  json: {
    ctx: {
      event: {
        event_id: eventResult.id,
        trigger_type: "daily_summary",
        trigger_reason: checkResult.trigger_reason,
        today: checkResult.today,
        trace_chain: [eventResult.id]
      },
      db_queries
    }
  }
}];

// Build LLM context from Execute_Queries results
const ctx = $json.ctx;
const db = ctx.db || {};

// Extract north star (use .row for single result)
const northStar = db.north_star?.row?.value || 'Not set';

// Extract activities (use .rows for arrays)
const activityItems = db.activities?.rows || [];

// Extract notes
const noteItems = db.notes?.rows || [];

// Extract breakdown
const breakdownItems = db.breakdown?.rows || [];

// Format activities list
let activitiesList = '';
if (activityItems.length === 0) {
  activitiesList = '(No activities logged today)';
} else {
  activityItems.forEach(item => {
    const time = new Date(item.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    activitiesList += `- ${time} [${item.category}] ${item.description}\n`;
  });
}

// Format notes list
let notesList = '';
if (noteItems.length === 0) {
  notesList = '(No notes captured today)';
} else {
  noteItems.forEach(item => {
    const time = new Date(item.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    notesList += `- ${time} [${item.category}] ${item.text}\n`;
  });
}

// Format category breakdown
let categoryBreakdown = '';
if (breakdownItems.length === 0) {
  categoryBreakdown = '(No data)';
} else {
  breakdownItems.forEach(item => {
    categoryBreakdown += `- ${item.category}: ${item.count}\n`;
  });
}

return [{
  json: {
    ctx,
    north_star: northStar,
    activities_list: activitiesList,
    notes_list: notesList,
    category_breakdown: categoryBreakdown,
    activity_count: activityItems.length,
    note_count: noteItems.length,
    inference_start: Date.now(),
    llm_input: {
      north_star: northStar,
      activity_count: activityItems.length,
      note_count: noteItems.length
    }
  }
}];

// Parse LLM response and prepare write queries for trace + projection
const prepareData = $('Prepare Summary Data').first().json;
const llmText = $json.text?.trim() || '';
const durationMs = Date.now() - prepareData.inference_start;
const ctx = prepareData.ctx;

// Build projection data
const projectionData = {
  timestamp: new Date().toISOString(),
  summary_date: ctx.event.today,
  text: llmText,
  activity_count: prepareData.llm_input?.activity_count || 0,
  note_count: prepareData.llm_input?.note_count || 0
};

// Prepare db_queries for Execute_Queries (Write)
const db_queries = [
  {
    key: 'trace',
    sql: `INSERT INTO traces (event_id, step_name, data, trace_chain)
          VALUES ($1::uuid, 'daily_summary', 
            jsonb_build_object(
              'input', $2::jsonb,
              'prompt', 'daily-summary-prompt',
              'completion', $3,
              'result', jsonb_build_object('summary', $3),
              'duration_ms', $4::integer
            ), $5::uuid[])
          RETURNING id, trace_chain || id AS updated_trace_chain`,
    params: [
      ctx.event.event_id,
      prepareData.llm_input,
      llmText,
      durationMs,
      ctx.event.trace_chain || []
    ]
  },
  {
    key: 'projection',
    sql: `INSERT INTO projections (trace_id, event_id, trace_chain, projection_type, data, status, timezone)
          VALUES ($1::uuid, $2::uuid, $3::uuid[], 'daily_summary', $4::jsonb, 'auto_confirmed',
            (SELECT value FROM config WHERE key = 'timezone'))
          RETURNING id`,
    params: [
      '$results.trace.row.id',
      ctx.event.event_id,
      '$results.trace.row.updated_trace_chain',
      projectionData
    ]
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    },
    summary_text: llmText
  }
}];

// Prepare for Discord - extract projection_id from write results
const ctx = $json.ctx;
const projectionId = ctx.db?.projection?.row?.id;

return [{
  json: {
    ctx,
    projection_id: projectionId,
    summary_text: $('Prepare Write Queries').first().json.summary_text
  }
}];

// Prepare final update queries after Discord post
const discordResult = $json;
const prepData = $('Prepare for Discord').first().json;
const ctx = prepData.ctx;

const db_queries = [
  {
    key: 'update_projection',
    sql: `UPDATE projections 
          SET data = data || jsonb_build_object(
            'discord_message_id', $1,
            'discord_channel_id', $2,
            'discord_guild_id', $3
          )
          WHERE id = $4::uuid
          RETURNING id`,
    params: [
      discordResult.id,
      $env.DISCORD_CHANNEL_OBSIDIAN_BOARD || '',
      $env.DISCORD_GUILD_ID || '',
      prepData.projection_id
    ]
  },
  {
    key: 'update_config',
    sql: `INSERT INTO config (key, value) VALUES ('last_summary_date', $1) 
          ON CONFLICT (key) DO UPDATE SET value = $1 
          RETURNING *`,
    params: [ctx.event.today]
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    }
  }
}];
