// Extract trigger_reason from caller, prepare event data
const input = $json;
const triggerReason = input.trigger_reason || 'cron';
const today = $now.toFormat('yyyy-MM-dd');

return [{ json: { should_run: true, today: today, trigger_reason: triggerReason } }];

// Initialize ctx with event_id and prepare db_queries for Query_DB
const eventResult = $json;
const checkResult = $("Prepare Event Data").first().json;

const db_queries = [
  {
    key: 'north_star',
    sql: `SELECT value FROM config WHERE key = 'north_star'`
  },
  {
    key: 'activities',
    sql: `SELECT 
            (p.data->>'timestamp')::timestamptz as timestamp,
            p.data->>'category' as category,
            p.data->>'description' as description
          FROM projections p
          WHERE p.projection_type = 'activity'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          ORDER BY (p.data->>'timestamp')::timestamptz`
  },
  {
    key: 'notes',
    sql: `SELECT 
            (p.data->>'timestamp')::timestamptz as timestamp,
            p.data->>'category' as category,
            p.data->>'text' as text
          FROM projections p
          WHERE p.projection_type = 'note'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          ORDER BY (p.data->>'timestamp')::timestamptz`
  },
  {
    key: 'breakdown',
    sql: `SELECT 
            p.data->>'category' as category,
            COUNT(*) as count
          FROM projections p
          WHERE p.projection_type = 'activity'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          GROUP BY p.data->>'category'
          ORDER BY count DESC`
  }
];

return [{
  json: {
    ctx: {
      event: {
        event_id: eventResult.id,
        trigger_type: "daily_summary",
        trigger_reason: checkResult.trigger_reason,
        today: checkResult.today,
        trace_chain: [eventResult.id]
      },
      db_queries
    }
  }
}];

// Build LLM context from Query_DB results
const ctx = $json.ctx;
const db = ctx.db || {};

// Extract north star
const northStar = db.north_star?.results?.[0]?.value || 'Not set';

// Extract activities
const activityItems = db.activities?.results || [];

// Extract notes
const noteItems = db.notes?.results || [];

// Extract breakdown
const breakdownItems = db.breakdown?.results || [];

// Format activities list
let activitiesList = '';
if (activityItems.length === 0) {
  activitiesList = '(No activities logged today)';
} else {
  activityItems.forEach(item => {
    const time = new Date(item.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    activitiesList += `- ${time} [${item.category}] ${item.description}\n`;
  });
}

// Format notes list
let notesList = '';
if (noteItems.length === 0) {
  notesList = '(No notes captured today)';
} else {
  noteItems.forEach(item => {
    const time = new Date(item.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    notesList += `- ${time} [${item.category}] ${item.text}\n`;
  });
}

// Format category breakdown
let categoryBreakdown = '';
if (breakdownItems.length === 0) {
  categoryBreakdown = '(No data)';
} else {
  breakdownItems.forEach(item => {
    categoryBreakdown += `- ${item.category}: ${item.count}\n`;
  });
}

return [{
  json: {
    ctx,
    north_star: northStar,
    activities_list: activitiesList,
    notes_list: notesList,
    category_breakdown: categoryBreakdown,
    activity_count: activityItems.length,
    note_count: noteItems.length,
    inference_start: Date.now(),
    llm_input: {
      north_star: northStar,
      activity_count: activityItems.length,
      note_count: noteItems.length
    }
  }
}];

// Parse LLM response and prepare for trace/projection storage
const prepareData = $('Prepare Summary Data').first().json;
const llmText = $json.text?.trim() || '';
const durationMs = Date.now() - prepareData.inference_start;
const ctx = prepareData.ctx;

// Format trace_chain for PostgreSQL
const traceChain = ctx.event.trace_chain || [];
const traceChainPg = '{' + traceChain.join(',') + '}';

return [{
  json: {
    ctx,
    summary_text: llmText,
    duration_ms: durationMs,
    trace_chain_pg: traceChainPg,
    llm_input: prepareData.llm_input,
    llm_input_json: JSON.stringify(prepareData.llm_input)
  }
}];

// Prepare projection data with trace_id
const traceResult = $json;
const parseData = $('Parse LLM Response').first().json;
const ctx = parseData.ctx;

const updatedTraceChain = traceResult.updated_trace_chain || [];
const updatedTraceChainPg = '{' + updatedTraceChain.join(',') + '}';

return [{
  json: {
    ctx,
    trace_id: traceResult.id,
    trace_chain_pg: updatedTraceChainPg,
    summary_text: parseData.summary_text,
    projection_data: {
      timestamp: new Date().toISOString(),
      summary_date: ctx.event.today,
      text: parseData.summary_text,
      activity_count: parseData.llm_input?.activity_count || 0,
      note_count: parseData.llm_input?.note_count || 0
    }
  }
}];

// Merge projection result back with summary text for Discord
const projResult = $json;
const prepData = $('Prepare Projection').first().json;

return [{
  json: {
    ctx: prepData.ctx,
    projection_id: projResult.id,
    summary_text: prepData.summary_text
  }
}];
