// Check if we should run summary (time matches AND not already run today)
const currentHour = $now.hour;
const todayDate = $now.toFormat('yyyy-MM-dd');
const inputItems = $input.all();

// Extract config values
let targetHour = 20; // default
let lastSummaryDate = null;

for (const item of inputItems) {
  if (item.json.key === 'summary_time') {
    const configTime = item.json.value;
    if (configTime.includes(':')) {
      targetHour = parseInt(configTime.split(':')[0]);
    } else {
      targetHour = parseInt(configTime);
    }
  } else if (item.json.key === 'last_summary_date') {
    lastSummaryDate = item.json.value;
  }
}

// Check 1: Is it the right hour?
if (currentHour !== targetHour) {
  return [];
}

// Check 2: Already ran today?
if (lastSummaryDate === todayDate) {
  return [];
}

// Continue - time matches and not run today
return [{ json: { should_run: true, today: todayDate, trigger_reason: 'cron' } }];

// Initialize ctx with event_id for downstream nodes
const eventResult = $json;
const checkResult = $('Check Should Run').first().json;

return [{
  json: {
    ctx: {
      event: {
        event_id: eventResult.id,
        trigger_type: 'daily_summary',
        trigger_reason: checkResult.trigger_reason,
        today: checkResult.today,
        trace_chain: []
      }
    }
  }
}];

// Safely merge all data for LLM summarization
// Handles empty results and errors gracefully

const allItems = $input.all();

// First item should be ctx from Initialize ctx
let ctx = null;
for (const item of allItems) {
  if (item.json.ctx) {
    ctx = item.json.ctx;
    break;
  }
}

if (!ctx) {
  throw new Error('ctx not found in merged items');
}

// Extract north star
let northStar = 'Not set';
const northStarItems = allItems.filter(item => item.json.value !== undefined && !item.json.error);
if (northStarItems.length > 0) {
  northStar = northStarItems[0].json.value;
}

// Extract activities (has 'description' field)
const activityItems = allItems.filter(item => 
  item.json.description !== undefined && 
  !item.json.error
);

// Extract notes (has 'text' field but not 'description')
const noteItems = allItems.filter(item => 
  item.json.text !== undefined && 
  item.json.description === undefined &&
  !item.json.error
);

// Extract breakdown (has 'count' field and 'category' but not description/text)
const breakdownItems = allItems.filter(item => 
  item.json.count !== undefined && 
  item.json.category !== undefined &&
  item.json.description === undefined &&
  item.json.text === undefined &&
  !item.json.error
);

// Format activities list
let activitiesList = '';
if (activityItems.length === 0) {
  activitiesList = '(No activities logged today)';
} else {
  activityItems.forEach(item => {
    const time = new Date(item.json.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    activitiesList += `- ${time} [${item.json.category}] ${item.json.description}\n`;
  });
}

// Format notes list
let notesList = '';
if (noteItems.length === 0) {
  notesList = '(No notes captured today)';
} else {
  noteItems.forEach(item => {
    const time = new Date(item.json.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    notesList += `- ${time} [${item.json.category}] ${item.json.text}\n`;
  });
}

// Format category breakdown
let breakdownText = '';
if (breakdownItems.length === 0) {
  breakdownText = '(No category data)';
} else {
  breakdownItems.forEach(item => {
    breakdownText += `- ${item.json.category}: ${item.json.count}\n`;
  });
}

// Build input for LLM (for trace storage)
const llmInput = {
  north_star: northStar,
  activity_count: activityItems.length,
  note_count: noteItems.length,
  activities: activityItems.map(i => i.json),
  notes: noteItems.map(i => i.json),
  breakdown: breakdownItems.map(i => i.json)
};

return [{
  json: {
    ctx,
    north_star: northStar,
    activities_list: activitiesList,
    notes_list: notesList,
    category_breakdown: breakdownText,
    activity_count: activityItems.length,
    note_count: noteItems.length,
    llm_input: llmInput,
    inference_start: Date.now()
  }
}];

// Parse LLM response and prepare for trace/projection storage
const prepareData = $('Prepare Summary Data').first().json;
const llmText = $json.text?.trim() || '';
const durationMs = Date.now() - prepareData.inference_start;
const ctx = prepareData.ctx;

// Format trace_chain for PostgreSQL
const traceChain = ctx.event.trace_chain || [];
const traceChainPg = '{' + traceChain.join(',') + '}';

return [{
  json: {
    ctx,
    summary_text: llmText,
    duration_ms: durationMs,
    trace_chain_pg: traceChainPg,
    llm_input: prepareData.llm_input,
    llm_input_json: JSON.stringify(prepareData.llm_input)
  }
}];

// Prepare projection data with trace_id
const traceResult = $json;
const parseData = $('Parse LLM Response').first().json;
const ctx = parseData.ctx;

const updatedTraceChain = traceResult.updated_trace_chain || [];
const updatedTraceChainPg = '{' + updatedTraceChain.join(',') + '}';

return [{
  json: {
    ctx,
    trace_id: traceResult.id,
    trace_chain_pg: updatedTraceChainPg,
    summary_text: parseData.summary_text,
    projection_data: {
      timestamp: new Date().toISOString(),
      summary_date: ctx.event.today,
      text: parseData.summary_text,
      activity_count: parseData.llm_input?.activity_count || 0,
      note_count: parseData.llm_input?.note_count || 0
    }
  }
}];

// Merge projection result back with summary text for Discord
const projResult = $json;
const prepData = $('Prepare Projection').first().json;

return [{
  json: {
    ctx: prepData.ctx,
    projection_id: projResult.id,
    summary_text: prepData.summary_text
  }
}];
