// Prepare event insert and initialize ctx
const input = $json;
const triggerReason = input.trigger_reason || 'cron';
const today = $now.toFormat('yyyy-MM-dd');
const idempotencyKey = `scheduled:daily_summary:${today}:${triggerReason}`;

return [{
  json: {
    ctx: {
      event: {
        trigger_type: 'daily_summary',
        trigger_reason: triggerReason,
        today: today
      },
      db_writes: [{
        key: 'event',
        sql: `INSERT INTO events (idempotency_key, event_type, payload)
              VALUES ($1, 'system', jsonb_build_object(
                'trigger_type', 'daily_summary',
                'trigger_reason', $2,
                'scheduled_date', $3
              ))
              ON CONFLICT (event_type, idempotency_key) DO UPDATE SET id = events.id
              RETURNING id, idempotency_key`,
        params: [idempotencyKey, triggerReason, today]
      }]
    }
  }
}];

// Build ctx with event_id and prepare batch queries
const ctx = $json.ctx;
const eventId = ctx.db.event.row.id;

return [{
  json: {
    ctx: {
      event: {
        ...ctx.event,
        event_id: eventId,
        trace_chain: [eventId]
      },
      db_queries: [
        {
          key: 'north_star',
          sql: "SELECT value FROM config WHERE key = 'north_star'"
        },
        {
          key: 'activities',
          sql: `SELECT 
            (p.data->>'timestamp')::timestamptz as timestamp,
            p.data->>'category' as category,
            p.data->>'description' as description
          FROM projections p
          WHERE p.projection_type = 'activity'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          ORDER BY (p.data->>'timestamp')::timestamptz`
        },
        {
          key: 'notes',
          sql: `SELECT 
            (p.data->>'timestamp')::timestamptz as timestamp,
            p.data->>'category' as category,
            p.data->>'text' as text
          FROM projections p
          WHERE p.projection_type = 'note'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          ORDER BY (p.data->>'timestamp')::timestamptz`
        },
        {
          key: 'breakdown',
          sql: `SELECT 
            p.data->>'category' as category,
            COUNT(*) as count
          FROM projections p
          WHERE p.projection_type = 'activity'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND DATE((p.data->>'timestamp')::timestamptz) = CURRENT_DATE
          GROUP BY p.data->>'category'
          ORDER BY count DESC`
        }
      ]
    }
  }
}];

// Build summary data from batch query results
const ctx = $json.ctx;

// Extract results using batch pattern
const northStar = ctx.db?.north_star?.results?.[0]?.value || 'Not set';
const activityItems = ctx.db?.activities?.results || [];
const noteItems = ctx.db?.notes?.results || [];
const breakdownItems = ctx.db?.breakdown?.results || [];

// Format activities list
let activitiesList = '';
if (activityItems.length === 0) {
  activitiesList = '(No activities logged today)';
} else {
  activityItems.forEach(item => {
    const time = new Date(item.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    activitiesList += `- ${time} [${item.category}] ${item.description}\n`;
  });
}

// Format notes list
let notesList = '';
if (noteItems.length === 0) {
  notesList = '(No notes captured today)';
} else {
  noteItems.forEach(item => {
    const time = new Date(item.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    notesList += `- ${time} [${item.category}] ${item.text}\n`;
  });
}

// Format breakdown
let breakdownList = '';
if (breakdownItems.length === 0) {
  breakdownList = '(No category data)';
} else {
  breakdownItems.forEach(item => {
    breakdownList += `- ${item.category}: ${item.count}\n`;
  });
}

return [{
  json: {
    ctx,
    north_star: northStar,
    activities_list: activitiesList.trim(),
    activity_count: activityItems.length,
    notes_list: notesList.trim(),
    note_count: noteItems.length,
    category_breakdown: breakdownList.trim(),
    inference_start: Date.now(),
    llm_input: {
      north_star: northStar,
      activity_count: activityItems.length,
      note_count: noteItems.length
    }
  }
}];

// Parse LLM response and prepare trace + projection writes (chained)
const prepareData = $('Prepare Summary Data').first().json;
const llmText = $json.text?.trim() || '';
const durationMs = Date.now() - prepareData.inference_start;
const ctx = prepareData.ctx;
const eventId = ctx.event.event_id;
const traceChain = ctx.event.trace_chain || [];
const today = ctx.event.today;

// Build input JSON for trace
const llmInputJson = JSON.stringify(prepareData.llm_input);

// Build projection data
const projectionData = JSON.stringify({
  timestamp: new Date().toISOString(),
  summary_date: today,
  text: llmText,
  activity_count: prepareData.llm_input?.activity_count || 0,
  note_count: prepareData.llm_input?.note_count || 0
});

return [{
  json: {
    ctx: {
      event: ctx.event,
      summary_text: llmText,
      db_writes: [
        {
          key: 'trace',
          sql: `INSERT INTO traces (event_id, step_name, data, trace_chain)
                VALUES ($1::uuid, 'daily_summary', jsonb_build_object(
                  'input', $2::jsonb,
                  'prompt', 'daily-summary-prompt',
                  'completion', $3,
                  'result', jsonb_build_object('summary', $3),
                  'duration_ms', $4::integer
                ), $5::uuid[])
                RETURNING id, trace_chain || id AS updated_trace_chain`,
          params: [eventId, llmInputJson, llmText, durationMs, traceChain]
        },
        {
          key: 'projection',
          sql: `INSERT INTO projections (trace_id, event_id, trace_chain, projection_type, data, status, timezone)
                VALUES ($1::uuid, $2::uuid, $3::uuid[], 'daily_summary', $4::jsonb, 'auto_confirmed',
                  (SELECT value FROM config WHERE key = 'timezone'))
                RETURNING id`,
          params: ['$results.trace.row.id', eventId, '$results.trace.row.updated_trace_chain', projectionData]
        }
      ]
    }
  }
}];

// Prepare for Discord post
const ctx = $json.ctx;

return [{
  json: {
    ctx,
    projection_id: ctx.db.projection.row.id,
    summary_text: ctx.summary_text
  }
}];

// Prepare final writes: update projection with message ID + update last summary date
const discordResult = $json;
const prevData = $('Prepare Discord Post').first().json;
const ctx = prevData.ctx;
const projectionId = prevData.projection_id;
const today = ctx.event.today;

return [{
  json: {
    ctx: {
      ...ctx,
      db_writes: [
        {
          key: 'update_projection',
          sql: `UPDATE projections 
                SET data = data || jsonb_build_object(
                  'discord_message_id', $1,
                  'discord_channel_id', $2,
                  'discord_guild_id', $3
                )
                WHERE id = $4::uuid
                RETURNING id`,
          params: [discordResult.id, $env.DISCORD_CHANNEL_OBSIDIAN_BOARD, $env.DISCORD_GUILD_ID, projectionId]
        },
        {
          key: 'update_config',
          sql: `INSERT INTO config (key, value) VALUES ('last_summary_date', $1) 
                ON CONFLICT (key) DO UPDATE SET value = $1 
                RETURNING *`,
          params: [today]
        }
      ]
    }
  }
}];
