// Extract trigger_reason from caller, default to 'cron'
const input = $json;
const triggerReason = input.trigger_reason || 'cron';
const now = new Date();
const idempotencyKey = `scheduled:nudge:${now.toISOString().slice(0, 16)}:${triggerReason}`;

return [{
  json: {
    idempotency_key: idempotencyKey,
    trigger_reason: triggerReason,
    timestamp: now.toISOString()
  }
}];

// Initialize ctx with event_id
const eventResult = $json;
const prepareData = $('Prepare Event').first().json;

return [{
  json: {
    ctx: {
      event: {
        event_id: eventResult.id,
        trigger_type: 'nudge',
        trigger_reason: prepareData.trigger_reason,
        timestamp: prepareData.timestamp,
        trace_chain: []
      }
    }
  }
}];

// Build LLM prompt context
const allItems = $input.all();

// Extract ctx
let ctx = null;
for (const item of allItems) {
  if (item.json.ctx) {
    ctx = item.json.ctx;
    break;
  }
}

if (!ctx) {
  throw new Error('ctx not found in merged items');
}

// Extract north star (fallback to introspection prompt)
let northStar = 'To determine my north star from deep introspection';
const northStarItems = allItems.filter(item => item.json.value !== undefined);
if (northStarItems.length > 0 && northStarItems[0].json.value) {
  northStar = northStarItems[0].json.value;
}

// Extract activities (has 'description' field)
const activityItems = allItems.filter(item => 
  item.json.description !== undefined && 
  !item.json.error
);

// Extract last note (has 'text' field but not 'description')
const noteItems = allItems.filter(item => 
  item.json.text !== undefined && 
  item.json.description === undefined &&
  !item.json.error
);

// Format activities list
let activitiesList = '';
let noActivities = true;
if (activityItems.length === 0) {
  activitiesList = '(No logged activity)';
} else {
  noActivities = false;
  activityItems.forEach(item => {
    const time = new Date(item.json.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    activitiesList += `- ${time} [${item.json.category}] ${item.json.description}\n`;
  });
}

// Format last note
let lastNote = '(None)';
if (noteItems.length > 0 && noteItems[0].json.text) {
  const note = noteItems[0].json;
  const time = new Date(note.timestamp).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
  lastNote = `${time} [${note.category}] ${note.text}`;
}

// Current timestamp for prompt
const timestamp = ctx.event.timestamp;

return [{
  json: {
    ctx,
    north_star: northStar,
    recent_activities: activitiesList,
    no_activities: noActivities,
    last_note: lastNote,
    timestamp: timestamp,
    activity_count: activityItems.length,
    inference_start: Date.now()
  }
}];

// Parse LLM response - determine SKIP/ENCOURAGE/REDIRECT
const prepareData = $('Prepare Prompt Data').first().json;
const llmText = $json.text?.trim() || '';
const durationMs = Date.now() - prepareData.inference_start;
const ctx = prepareData.ctx;

// Parse the decision from first line
const lines = llmText.split('\n').filter(l => l.trim());
const decision = (lines[0] || '').trim().toUpperCase();

// Extract message (everything after first line)
const message = lines.slice(1).join('\n').trim();

// Determine if we should skip
const shouldSkip = decision === 'SKIP' || !['ENCOURAGE', 'REDIRECT'].includes(decision);

// Format trace_chain for PostgreSQL (with null safety)
const traceChain = ctx?.event?.trace_chain || [];
const traceChainPg = '{' + traceChain.join(',') + '}';

return [{
  json: {
    ctx,
    decision: decision,
    message: message,
    should_skip: shouldSkip,
    duration_ms: durationMs,
    trace_chain_pg: traceChainPg,
    llm_response: llmText,
    llm_input: {
      north_star: prepareData.north_star,
      recent_activities: prepareData.recent_activities,
      last_note: prepareData.last_note,
      timestamp: prepareData.timestamp,
      activity_count: prepareData.activity_count
    }
  }
}];

// Merge trace result with parsed LLM data
const traceResult = $json;
const parseData = $('Parse LLM Response').first().json;

const updatedTraceChain = traceResult.updated_trace_chain || [];
const updatedTraceChainPg = '{' + updatedTraceChain.join(',') + '}';

return [{
  json: {
    ctx: parseData.ctx,
    trace_id: traceResult.id,
    trace_chain_pg: updatedTraceChainPg,
    decision: parseData.decision,
    message: parseData.message,
    should_skip: parseData.should_skip
  }
}];

// Merge projection result for Discord
const projResult = $json;
const mergeData = $('Merge Trace Result').first().json;

return [{
  json: {
    ctx: mergeData.ctx,
    projection_id: projResult.id,
    message: mergeData.message,
    decision: mergeData.decision
  }
}];
