// Extract trigger_reason from caller, default to 'cron'
const input = $json;
const triggerReason = input.trigger_reason || 'cron';
const now = new Date();
const idempotencyKey = `scheduled:nudge:${now.toISOString().slice(0, 16)}:${triggerReason}`;

return [{
  json: {
    idempotency_key: idempotencyKey,
    trigger_reason: triggerReason,
    timestamp: now.toISOString()
  }
}];

// Initialize ctx with event_id and prepare batch database queries
const eventResult = $json;
const prepareData = $("Prepare Event").first().json;

const db_queries = [
  {
    key: 'north_star',
    sql: `SELECT value FROM config WHERE key = 'north_star'`
  },
  {
    key: 'activities',
    sql: `SELECT 
            (p.data->>'timestamp')::timestamptz as timestamp,
            p.data->>'category' as category,
            p.data->>'description' as description
          FROM projections p
          WHERE p.projection_type = 'activity'
            AND p.status IN ('auto_confirmed', 'confirmed')
            AND (p.data->>'timestamp')::timestamptz >= NOW() - INTERVAL '4 hours'
          ORDER BY (p.data->>'timestamp')::timestamptz DESC`
  },
  {
    key: 'last_note',
    sql: `SELECT 
            (p.data->>'timestamp')::timestamptz as timestamp,
            p.data->>'category' as category,
            p.data->>'text' as text
          FROM projections p
          WHERE p.projection_type = 'note'
            AND p.status IN ('auto_confirmed', 'confirmed')
          ORDER BY (p.data->>'timestamp')::timestamptz DESC
          LIMIT 1`
  },
  {
    key: 'timezone',
    sql: `SELECT value as timezone FROM config WHERE key = 'timezone'`
  }
];

return [{
  json: {
    ctx: {
      event: {
        event_id: eventResult.id,
        trigger_type: "nudge",
        trigger_reason: prepareData.trigger_reason,
        timestamp: prepareData.timestamp,
        timezone: eventResult.timezone || 'UTC',
        trace_chain: [eventResult.id]
      },
      db_queries
    }
  }
}];

// Build LLM prompt context from Execute_Queries results
const ctx = $json.ctx;
const db = ctx.db || {};

// Extract timezone from db results, fallback to event timezone or UTC
const timezone = db.timezone?.row?.timezone || ctx.event.timezone || 'UTC';

// Helper to format time in user's timezone
const formatTime = (isoString) => {
  try {
    return new Date(isoString).toLocaleTimeString('en-US', { 
      timeZone: timezone, 
      hour: 'numeric', 
      minute: '2-digit' 
    });
  } catch (e) {
    // Fallback if timezone is invalid
    return new Date(isoString).toLocaleTimeString('en-US', { 
      hour: 'numeric', 
      minute: '2-digit' 
    });
  }
};

// Extract north star (fallback to introspection prompt)
const northStar = db.north_star?.row?.value || 'To determine my north star from deep introspection';

// Extract activities from db results (use .rows for arrays)
const activityItems = db.activities?.rows || [];

// Extract last note from db results (use .row for LIMIT 1)
const lastNote = db.last_note?.row;

// Format activities list with user's timezone
let activitiesList = '';
if (activityItems.length === 0) {
  activitiesList = '(No logged activity)';
} else {
  activityItems.forEach(item => {
    const time = formatTime(item.timestamp);
    activitiesList += `- ${time}: [${item.category}] ${item.description}\n`;
  });
}

// Format last note
let lastNoteText = '(No recent notes)';
if (lastNote) {
  const time = formatTime(lastNote.timestamp);
  lastNoteText = `${time}: [${lastNote.category}] ${lastNote.text}`;
}

// Get current time in user's timezone
let currentTime;
try {
  currentTime = new Date().toLocaleString('en-US', {
    timeZone: timezone,
    weekday: 'long',
    hour: 'numeric',
    minute: '2-digit'
  });
} catch (e) {
  currentTime = new Date().toLocaleString('en-US', {
    weekday: 'long',
    hour: 'numeric',
    minute: '2-digit'
  });
}

return [{
  json: {
    ctx,
    north_star: northStar,
    recent_activities: activitiesList.trim(),
    last_note: lastNoteText,
    timestamp: currentTime,
    activity_count: activityItems.length,
    inference_start: Date.now()
  }
}];

// Parse LLM response and prepare trace write query
const prepareData = $('Prepare Prompt Data').first().json;
const llmText = $json.text?.trim() || '';
const durationMs = Date.now() - prepareData.inference_start;
const ctx = prepareData.ctx;

// Parse the decision from first line
const lines = llmText.split('\n').filter(l => l.trim());
const decision = (lines[0] || '').trim().toUpperCase();

// Extract message (everything after first line)
const message = lines.slice(1).join('\n').trim();

// Determine if we should skip
const shouldSkip = decision === 'SKIP' || !['ENCOURAGE', 'REDIRECT'].includes(decision);

// Prepare db_queries for trace INSERT
const db_queries = [
  {
    key: 'trace',
    sql: `INSERT INTO traces (event_id, step_name, data, trace_chain)
          VALUES ($1::uuid, 'nudge',
            jsonb_build_object(
              'input', $2::jsonb,
              'prompt', 'nudge-prompt',
              'completion', $3,
              'result', jsonb_build_object(
                'decision', $4,
                'message', $5,
                'should_skip', $6::boolean
              ),
              'duration_ms', $7::integer
            ), $8::uuid[])
          RETURNING id, trace_chain || id AS updated_trace_chain`,
    params: [
      ctx.event.event_id,
      {
        north_star: prepareData.north_star,
        recent_activities: prepareData.recent_activities,
        last_note: prepareData.last_note,
        timestamp: prepareData.timestamp,
        activity_count: prepareData.activity_count
      },
      llmText,
      decision,
      message,
      shouldSkip,
      durationMs,
      ctx.event.trace_chain || []
    ]
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    },
    decision,
    message,
    should_skip: shouldSkip
  }
}];

// Check if should skip and prepare for projection branch
const ctx = $json.ctx;
const traceRow = ctx.db?.trace?.row;
const prepData = $('Prepare Trace Query').first().json;

return [{
  json: {
    ctx,
    trace_id: traceRow?.id,
    updated_trace_chain: traceRow?.updated_trace_chain || [],
    decision: prepData.decision,
    message: prepData.message,
    should_skip: prepData.should_skip
  }
}];

// Prepare projection + final update queries
const checkData = $json;
const ctx = checkData.ctx;

const projectionData = {
  timestamp: new Date().toISOString(),
  decision: checkData.decision,
  text: checkData.message
};

const db_queries = [
  {
    key: 'projection',
    sql: `INSERT INTO projections (trace_id, event_id, trace_chain, projection_type, data, status, timezone)
          VALUES ($1::uuid, $2::uuid, $3::uuid[], 'nudge', $4::jsonb, 'auto_confirmed', $5)
          RETURNING id`,
    params: [
      checkData.trace_id,
      ctx.event.event_id,
      checkData.updated_trace_chain,
      projectionData,
      ctx.event.timezone
    ]
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    },
    message: checkData.message,
    decision: checkData.decision
  }
}];

// Prepare for Discord post
const ctx = $json.ctx;
const projectionId = ctx.db?.projection?.row?.id;
const prepData = $('Prepare Projection Query').first().json;

return [{
  json: {
    ctx,
    projection_id: projectionId,
    message: prepData.message
  }
}];

// Prepare final update query with Discord message ID
const discordResult = $json;
const prepData = $('Prepare for Discord').first().json;
const ctx = prepData.ctx;

const db_queries = [
  {
    key: 'update_projection',
    sql: `UPDATE projections 
          SET data = data || jsonb_build_object(
            'discord_message_id', $1,
            'discord_channel_id', $2,
            'discord_guild_id', $3
          )
          WHERE id = $4::uuid
          RETURNING id`,
    params: [
      discordResult.id,
      $env.DISCORD_CHANNEL_ARCANE_SHELL || '',
      $env.DISCORD_GUILD_ID || '',
      prepData.projection_id
    ]
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    }
  }
}];
