// Build lookup query for Execute_Queries
const ctx = $json.ctx;

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries: [{
        key: 'original',
        sql: `-- Look up the original event that created projections for this message
-- Also get the emoji mapping config for correction types
WITH original AS (
  SELECT 
    e.id as event_id,
    e.payload->>'discord_message_id' as message_id,
    e.payload->>'clean_text' as clean_text,
    e.payload->>'discord_channel_id' as channel_id,
    e.payload->>'discord_guild_id' as guild_id,
    e.payload->>'author_login' as author_login,
    e.payload as original_payload
  FROM events e
  WHERE e.payload->>'discord_message_id' = $1
    AND e.event_type = 'discord_message'
  ORDER BY e.received_at DESC
  LIMIT 1
),
emoji_config AS (
  SELECT value FROM config WHERE key = 'emoji_mapping'
)
SELECT 
  o.*,
  ec.value as emoji_mapping
FROM original o
CROSS JOIN emoji_config ec;`,
        params: [ctx.event.message_id]
      }]
    }
  }
}];

// Prepare correction context from lookup result
const ctx = $json.ctx;
const original = ctx.db?.original?.row;

if (!original || !original.event_id) {
  return {
    ctx,
    has_original: false,
    error: 'No original event found for this message'
  };
}

// Parse emoji mapping - Postgres returns JSON as string
const rawMapping = original.emoji_mapping;
const emojiMapping = typeof rawMapping === 'string' ? JSON.parse(rawMapping) : (rawMapping || {});
const types = emojiMapping.types || {};
const actions = emojiMapping.actions || {};
const emoji = ctx.event.emoji;

// Determine correction type
let correctionType = null;
let correctionTag = null;
let isVoidOnly = false;

if (types[emoji]) {
  correctionType = types[emoji].type;
  correctionTag = types[emoji].tag;
} else if (actions[emoji] === 'void') {
  isVoidOnly = true;
}

// Pre-compute values for Postgres queries to avoid n8n expression parsing issues
const messageUrl = `https://discord.com/channels/${original.guild_id}/${original.channel_id}/${original.message_id}`;
const idempotencyKey = `correction_${original.message_id}_${Date.now()}`;

return {
  ctx,
  has_original: true,
  original: {
    event_id: original.event_id,
    message_id: original.message_id,
    clean_text: original.clean_text,
    channel_id: original.channel_id,
    guild_id: original.guild_id,
    author_login: original.author_login,
    payload: original.original_payload,
    message_url: messageUrl
  },
  correction: {
    emoji: emoji,
    type: correctionType,
    tag: correctionTag,
    is_void_only: isVoidOnly,
    idempotency_key: idempotencyKey
  }
};

// Build queries for correction event creation and voiding projections
// Uses $results chaining: correction event id -> void projections
const data = $json;
const ctx = data.ctx;
const original = data.original;
const correction = data.correction;

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries: [
        {
          key: 'correction_event',
          sql: `-- Create correction event for audit trail
INSERT INTO events (idempotency_key, event_type, payload)
VALUES (
  $1,
  'user_correction',
  jsonb_build_object(
    'original_event_id', $2,
    'original_message_id', $3,
    'correction_emoji', $4,
    'correction_type', $5,
    'is_void_only', $6,
    'triggered_by_event_id', $7
  )
)
ON CONFLICT (event_type, idempotency_key) DO NOTHING
RETURNING id;`,
          params: [
            correction.idempotency_key,
            original.event_id,
            original.message_id,
            correction.emoji,
            correction.type || 'void',
            correction.is_void_only,
            ctx.event.event_id
          ]
        },
        {
          key: 'voided',
          sql: `-- Void ALL projections for this Discord message (by message_url)
-- This catches both original projections and any corrected ones
UPDATE projections
SET 
  status = 'voided',
  voided_at = NOW(),
  voided_reason = 'user_correction',
  voided_by_event_id = $1::uuid
WHERE data->>'message_url' = $2
  AND status IN ('pending', 'auto_confirmed', 'confirmed')
RETURNING id, projection_type, data->>'description' as description, data->>'text' as text;`,
          params: [
            '$results.correction_event.row.id',
            original.message_url
          ]
        }
      ]
    },
    original: original,
    correction: correction
  }
}];

// Build list of emojis to remove based on voided projection types
const ctx = $json.ctx;
const original = $('Build Correction Queries').first().json.original;
const channelId = original.channel_id;
const messageId = original.message_id;

// Get voided projection types from Execute_Queries result
const voidedRows = ctx.db?.voided?.rows || [];
const voidedTypes = voidedRows.map(item => item.projection_type).filter(Boolean);

// Map projection types to their emojis
const typeToEmoji = {
  'activity': 'ðŸ”˜',
  'note': 'ðŸ“',
  'todo': 'âœ…'
};

// Only remove emojis for the projection types that were actually voided
const emojisToRemove = [...new Set(voidedTypes.map(t => typeToEmoji[t]).filter(Boolean))];

if (emojisToRemove.length === 0) {
  // No emojis to remove, return skip marker
  return [{ json: { skip: true, ctx } }];
}

return emojisToRemove.map(emoji => ({
  json: {
    channel_id: channelId,
    message_id: messageId,
    emoji: emoji,
    encoded_emoji: encodeURIComponent(emoji),
    skip: false,
    ctx
  }
}));

// After removing emojis, check if we need to re-extract or just void
const prepareData = $('Build Correction Queries').first().json;
const ctx = $('Execute Correction Queries').first().json.ctx;

// Get correction event ID from Execute_Queries result
const correctionEventId = ctx.db?.correction_event?.row?.id;

// Collect voided projection IDs for linking to new projection
const voidedRows = ctx.db?.voided?.rows || [];
const voidedProjectionIds = voidedRows.map(item => item.id).filter(Boolean);

return {
  ctx,
  original: prepareData.original,
  correction: prepareData.correction,
  correction_event_id: correctionEventId,
  voided_projection_ids: voidedProjectionIds,
  should_reextract: !prepareData.correction.is_void_only && !!prepareData.correction.type
};

// Build ctx for Capture_Projection, reusing original event data
const data = $json;
const original = data.original;
const correction = data.correction;
const existingCtx = data.ctx || {};

// Map correction type to projection type
const typeMap = {
  'activity': 'activity',
  'note': 'note',
  'todo': 'todo'
};

const projectionType = typeMap[correction.type] || 'note';

// Build new trace chain starting from correction event
const traceChain = [data.correction_event_id];
const traceChainPg = '{' + traceChain.join(',') + '}';

// Construct message_url from original data
const messageUrl = `https://discord.com/channels/${original.guild_id}/${original.channel_id}/${original.message_id}`;

// Pass voided projection IDs for supersedes linking
const voidedProjectionIds = data.voided_projection_ids || [];

return {
  ctx: {
    ...existingCtx,
    event: {
      event_id: data.correction_event_id,
      event_type: 'user_correction',
      timestamp: new Date().toISOString(),
      timezone: existingCtx.event?.timezone,
      guild_id: original.guild_id,
      channel_id: original.channel_id,
      message_id: original.message_id,
      message_url: messageUrl,
      author_login: original.author_login,
      raw_text: original.clean_text,
      clean_text: original.clean_text,
      tag: correction.tag,
      trace_chain: traceChain,
      trace_chain_pg: traceChainPg
    },
    projection: {
      type: projectionType
    },
    correction: {
      original_event_id: original.event_id,
      emoji: correction.emoji,
      voided_projection_ids: voidedProjectionIds
    }
  }
};
