// Filter: Skip if this is a parent workflow wrapping a child error
// Parent errors have errorResponse (the wrapped child error)
const errorData = $input.first().json;
const error = errorData.execution?.error || {};

// If this error is just wrapping a sub-workflow error, skip it
if (error.errorResponse) {
  return []; // Return empty to stop flow
}

// Continue processing - prepare query for recent event lookup
return [{
  json: {
    error_data: errorData,
    db_queries: [{
      key: 'recent_event',
      sql: `SELECT 
              id,
              payload->>'discord_guild_id' as guild_id,
              payload->>'discord_channel_id' as channel_id,
              payload->>'discord_message_id' as message_id,
              payload->>'clean_text' as clean_text
            FROM events 
            WHERE received_at > NOW() - interval '30 seconds'
              AND event_type = 'discord_message'
            ORDER BY received_at DESC 
            LIMIT 1`
    }]
  }
}];

// Extract error details and merge with event context
// Read error_data from earlier node and event from Query_DB result
const errorData = $json.error_data || $('Filter Duplicate Errors').first().json.error_data;
const recentEventResults = $json.ctx?.db?.recent_event?.results || [];
const eventData = recentEventResults[0] || {};

const execution = errorData.execution || {};
const workflow = errorData.workflow || {};
const error = execution.error || {};

// Get the node that failed
const failedNode = error.node?.name || execution.lastNodeExecuted || 'Unknown';
const errorMessage = error.message || 'No error message';

// Get workflow info
const workflowName = workflow.name || 'Unknown';
const executionId = execution.id || 'N/A';

// Get Discord context from recent event
const guildId = eventData.guild_id || null;
const channelId = eventData.channel_id || null;
const messageId = eventData.message_id || null;
const cleanText = eventData.clean_text || '';

// Try to extract from error URL if available
let extractedChannelId = channelId;
let extractedMessageId = messageId;

if (!channelId && error.context?.request?.uri) {
  const uri = error.context.request.uri;
  // Parse: https://discord.com/api/v10/channels/{channel_id}/messages/{message_id}/...
  const match = uri.match(/channels\/([0-9]+)\/messages\/([0-9]+)/);
  if (match) {
    extractedChannelId = match[1];
    extractedMessageId = match[2];
  }
}

// Build condensed Discord message
let content = `❌ **${workflowName}** → ${failedNode}\n`;
content += `${errorMessage}`;

if (cleanText) {
  const preview = cleanText.length > 50 ? cleanText.substring(0, 50) + '...' : cleanText;
  content += `\n📝 "${preview}"`;
}

content += `\n\`exec:${executionId}\``;

// Truncate if needed (Discord 2000 char limit)
if (content.length > 1900) {
  content = content.substring(0, 1900) + '...';
}

return [{
  json: {
    content: content,
    guild_id: guildId || $env.DISCORD_GUILD_ID,
    channel_id: extractedChannelId,
    message_id: extractedMessageId,
    can_react: !!(extractedChannelId && extractedMessageId)
  }
}];
