// Prepare for multi-capture
const ctx = $json.ctx;

return [{
  json: {
    ctx: ctx,
    inference_start: Date.now()
  }
}];

// Parse LLM response, filter by confidence, build trace query
const EMOJI = { activity: 'ðŸ”˜', note: 'ðŸ“', todo: 'âœ…' };
const CONFIDENCE_THRESHOLD = 0.5;

const llmText = $json.text?.trim() || '';
const prepareItem = $('Prepare Capture').first().json;
const ctx = prepareItem.ctx;
const durationMs = Date.now() - prepareItem.inference_start;

// Full rendered prompt (template + input)
const fullPrompt = `You are an extraction agent for a life-tracking system. Analyze the message and extract all relevant items.

## Message to Analyze

"${ctx.event.clean_text}"

## Extraction Types

### Activity (what the user is doing NOW)
Extract if the message describes a CURRENT or RECENT action by the user.
- **Categories:** work, leisure, study, health, sleep, relationships, admin
- **Indicators:** "I am", "I'm", "-ing verbs", "just did", present/recent past tense

### Note (observation, insight, or fact worth remembering)
Extract if the message contains knowledge, observations, or reflections.
- **Categories:** reflection (about self), fact (about world/others)
- **Indicators:** observations about things/people, realizations, ideas, decisions

### Todo (actionable task to complete)
Extract if the message contains a clear task to do later.
- **Priority:** high, medium, low
- **Indicators:** "need to", "should", "have to", "TODO", future tasks

## Key Rules

1. A message can have 0, 1, 2, or 3 extractions
2. Set confidence 0.0-1.0 based on how clearly the message indicates each type
3. Only include extractions with confidence >= 0.5
4. Prefer fewer high-confidence extractions over many low-confidence ones
5. Activity describes what I'M doing; Note describes observations about anything else

## Output Format

Output ONLY valid JSON, no explanation:

{"activity": {"category": "work", "description": "debugging auth", "confidence": 0.92}, "note": {"category": "reflection", "text": "noticed pattern in logs", "confidence": 0.78}, "todo": null}`;

// Format trace_chain for PostgreSQL
const traceChain = ctx.event.trace_chain || [];
const traceChainPg = '{' + traceChain.join(',') + '}';

// Parse JSON from LLM output
let parsed = { activity: null, note: null, todo: null };
let parseError = null;

try {
  let jsonStr = llmText;
  const codeBlock = llmText.match(/```(?:json)?\s*([\s\S]*?)```/);
  if (codeBlock) jsonStr = codeBlock[1].trim();
  const objMatch = jsonStr.match(/\{[\s\S]*\}/);
  if (objMatch) jsonStr = objMatch[0];
  parsed = JSON.parse(jsonStr);
} catch (e) {
  parseError = e.message;
}

// Build captures array
const captures = [];
const emojis = [];

if (parsed.activity?.confidence >= CONFIDENCE_THRESHOLD) {
  captures.push({
    type: 'activity',
    emoji: EMOJI.activity,
    data: {
      timestamp: new Date().toISOString(),
      category: parsed.activity.category || 'work',
      description: parsed.activity.description || ctx.event.clean_text,
      message_url: ctx.event.message_url,
      confidence: parsed.activity.confidence
    }
  });
  emojis.push(EMOJI.activity);
}

if (parsed.note?.confidence >= CONFIDENCE_THRESHOLD) {
  captures.push({
    type: 'note',
    emoji: EMOJI.note,
    data: {
      timestamp: new Date().toISOString(),
      category: parsed.note.category || 'reflection',
      text: parsed.note.text || ctx.event.clean_text,
      message_url: ctx.event.message_url,
      confidence: parsed.note.confidence
    }
  });
  emojis.push(EMOJI.note);
}

if (parsed.todo?.confidence >= CONFIDENCE_THRESHOLD) {
  captures.push({
    type: 'todo',
    emoji: EMOJI.todo,
    data: {
      timestamp: new Date().toISOString(),
      priority: parsed.todo.priority || 'medium',
      text: parsed.todo.text || ctx.event.clean_text,
      message_url: ctx.event.message_url,
      confidence: parsed.todo.confidence
    }
  });
  emojis.push(EMOJI.todo);
}

// Fallback: if nothing captured, create low-confidence note
if (captures.length === 0) {
  captures.push({
    type: 'note',
    emoji: EMOJI.note,
    data: {
      timestamp: new Date().toISOString(),
      category: 'reflection',
      text: ctx.event.clean_text,
      message_url: ctx.event.message_url,
      confidence: 0.3
    }
  });
  emojis.push(EMOJI.note);
}

// Pre-stringify the result for postgres jsonb
const resultJson = JSON.stringify(captures.map(c => ({ type: c.type, confidence: c.data.confidence })));

// Return with ctx.db_queries for Execute_Queries
return [{
  json: {
    ctx: {
      ...ctx,
      db_queries: [{
        key: 'trace',
        sql: `INSERT INTO traces (event_id, step_name, data, trace_chain)
VALUES (
  $1::uuid,
  'multi_capture',
  jsonb_build_object(
    'prompt', $2,
    'input', jsonb_build_object('text', $3),
    'completion', $4,
    'result', $5::jsonb,
    'model', 'xiaomi/mimo-v2-flash:free',
    'duration_ms', $6::integer,
    'capture_count', $7::integer
  ),
  $8::uuid[]
)
RETURNING id, trace_chain || id AS updated_trace_chain;`,
        params: [
          ctx.event.event_id,
          fullPrompt,
          ctx.event.clean_text,
          llmText,
          resultJson,
          durationMs,
          captures.length,
          traceChainPg
        ]
      }]
    },
    captures,
    emojis,
    total_captures: captures.length,
    raw_response: llmText,
    parse_error: parseError,
    duration_ms: durationMs
  }
}];

// Split captures into individual items for projection storage
// Input: Store LLM Trace result (ctx with db.trace)
// Output: One item per capture, each with trace_id attached

const ctx = $json.ctx;
const parseResult = $('Parse Response').first().json;

const llmTraceId = ctx.db?.trace?.row?.id;
const updatedTraceChain = ctx.db?.trace?.row?.updated_trace_chain || [];
const updatedTraceChainPg = '{' + updatedTraceChain.join(',') + '}';

// Split captures array into individual items, each with its own db_queries
return parseResult.captures.map(capture => ({
  json: {
    ctx: {
      ...ctx,
      db_queries: [{
        key: 'projection',
        sql: `INSERT INTO projections (trace_id, event_id, trace_chain, projection_type, data, status, timezone)
VALUES (
  $1::uuid,
  $2::uuid,
  $3::uuid[],
  $4,
  $5::jsonb,
  'auto_confirmed',
  $6
)
RETURNING *;`,
        params: [
          llmTraceId,
          ctx.event.event_id,
          updatedTraceChainPg,
          capture.type,
          JSON.stringify(capture.data),
          ctx.event.timezone
        ]
      }]
    },
    capture
  }
}));

// Collect results for logging with standard emojis
const parseResult = $('Parse Response').first().json;

// Standard emoji mapping
const typeEmojis = {
  'activity': 'ðŸ”˜',
  'note': 'ðŸ“',
  'todo': 'ðŸ”²'
};

// Map capture types to standard emojis
const emojis = parseResult.captures.map(c => typeEmojis[c.type] || 'ðŸ“¦');

// Build verbose config query
return [{
  json: {
    ctx: {
      ...parseResult.ctx,
      db_queries: [{
        key: 'verbose_config',
        sql: `SELECT value FROM config WHERE key = 'verbose'`,
        params: []
      }]
    },
    emojis: emojis,
    duration_ms: parseResult.duration_ms,
    projection_count: parseResult.total_captures
  }
}];

// Convert emojis array to individual items for batch processing
const parseResult = $('Parse Response').first().json;

// Standard emoji mapping
const typeEmojis = {
  'activity': 'ðŸ”˜',
  'note': 'ðŸ“',
  'todo': 'ðŸ”²'
};

// Map capture types to standard emojis
const emojis = parseResult.captures.map(c => typeEmojis[c.type] || 'ðŸ“¦');

return emojis.map(emoji => ({
  json: {
    ctx: parseResult.ctx,
    emoji: emoji
  }
}));

// Merge verbose config result into ctx
const collectResults = $('Collect Results').first().json;
const ctx = $json.ctx;
const verboseValue = ctx.db?.verbose_config?.row?.value;

return [{
  json: {
    ctx: {
      ...ctx,
      verbose: verboseValue === 'true' || verboseValue === true
    },
    emojis: collectResults.emojis,
    duration_ms: collectResults.duration_ms,
    projection_count: collectResults.projection_count
  }
}];

// Extract text for embedding from projection data
const ctx = $json.ctx;
const projection = ctx.db?.projection?.row || {};
const data = projection.data || {};
const embeddingText = data.description || data.text || '';

return [{
  json: {
    projection_id: projection.id,
    embedding_text: embeddingText
  }
}];

// Build embedding insert query for Execute_Queries
const prepareData = $('Prepare Embedding Text').first().json;
const embeddings = $json.embeddings;

// Check if we have a valid embedding
if (!embeddings || !embeddings[0]) {
  return [];  // Skip if no embedding
}

const embeddingVector = '[' + embeddings[0].join(',') + ']';

return [{
  json: {
    ctx: {
      db_queries: [{
        key: 'embedding',
        sql: `-- Insert embedding for the projection (fire-and-forget)
INSERT INTO embeddings (projection_id, model, embedding_data, embedded_text, embedding)
SELECT 
  $1::uuid,
  'all-MiniLM-L6-v2',
  '{}',
  $2,
  $3::vector
WHERE $3 IS NOT NULL
ON CONFLICT DO NOTHING;`,
        params: [
          prepareData.projection_id,
          prepareData.embedding_text,
          embeddingVector
        ]
      }]
    }
  }
}];
