// Parse LLM output in pipe format: "category|score" (Option E schema)
let text = $input.first().json.text.trim();

const event = $('Set Inference Start').first().json.event;
const inferenceStart = $('Set Inference Start').first().json.inference_start;
const durationMs = Date.now() - inferenceStart;

try {
  // Parse pipe format: "work|98\nadmin|2"
  const lines = text.split('\n').filter(line => line.includes('|'));
  const scores = {};
  let topCategory = null;
  let topScore = 0;

  lines.forEach(line => {
    const [category, scoreStr] = line.split('|').map(s => s.trim());
    const score = parseInt(scoreStr);

    if (!isNaN(score)) {
      scores[category] = score;

      if (score > topScore) {
        topScore = score;
        topCategory = category;
      }
    }
  });

  // Ensure we have at least one category
  if (!topCategory) {
    throw new Error('No valid categories found in LLM response');
  }

  // Build full prompt text (filled prompt for Option E)
  const promptText = `You are an activity classifier for a life-tracking system.

Assign probability scores (0-100) to each category. Scores must add up to exactly 100.

## Categories

work â†’ Productive, focused effort on projects, coding, building, debugging, refining systems, testing, or task-oriented development.
leisure â†’ Relaxed, non-productive enjoyment: scrolling, watching videos, browsing, casual reading, coffee breaks.
study â†’ Intentional learning or skill-building not directly tied to immediate project work.
health â†’ Physical or mental self-care: exercise, eating meals, hygiene, mindfulness.
sleep â†’ Going to bed, waking up, napping, or sleep transitions.
relationships â†’ Social interaction: chatting with friends/family/partners, or personal/social AI conversations.
admin â†’ Organizational/maintenance tasks: planning schedules, managing todos, fixing tools/setup, meta work on tracking system.

## Key Guidelines
- Heavily favor the most dominant theme.
- Sleep-related messages â†’ give sleep the highest score.
- Project-related AI chats â†’ work.
- Eating/drinking â†’ health (split with leisure if combined).
- Meta work on tracking system â†’ admin (or work if core building).
- Omit any category with score â‰¤5 (treat as 0).

Output ONLY lines for categories with score >5, in any order:
category_name|X

(Printed scores must sum to exactly 100, integers only)

# Message to classify:
${event.clean_text}`;

  return [{
    json: {
      // Option E schema fields for trace
      input_text: event.clean_text,
      prompt_text: promptText,
      completion_text: text,
      result_category: topCategory,
      result_confidence: topScore / 100,  // Convert to 0-1 range
      result_all_scores: scores,
      duration_ms: durationMs,

      // Projection fields
      category: topCategory,
      confidence: topScore / 100,
      all_scores: scores,
      description: event.clean_text,  // Use original clean_text as description
      ...event  // Pass through all event fields
    }
  }];
} catch (error) {
  // Fallback: if parsing fails, default to "admin" category
  return [{
    json: {
      input_text: event.clean_text,
      prompt_text: '',
      completion_text: text,
      result_category: "admin",
      result_confidence: 0.5,
      result_all_scores: {},
      duration_ms: durationMs,

      category: "admin",
      confidence: 0.5,
      all_scores: {},
      description: event.clean_text,
      parse_error: error.message,
      ...event
    }
  }];
}

