// Get conversation ID
const conversationId = $('Get Conversation ID').first().json.id;

// Get all thread messages
const messages = $input.all().toReversed();
// Format thread history for LLM
const threadHistory = messages.map(msg => {
  return `${msg.json.role.toUpperCase()}: ${msg.json.text}`;
}).join('\n');

// Get original event
const event = $('Execute Workflow Trigger').first().json.event;

return [{
  json: {
    ...event,
    conversation_id: conversationId,
    thread_history: threadHistory,
  }
}];

// Parse LLM output in pipe format: "type|text"
let llmOutput = $input.first().json.text.trim();
const context = $('Prepare Context').first().json;

try {
  const lines = llmOutput.split('\n').filter(line => line.includes('|'));
  const extractions = [];
  let displayOrder = 1;

  lines.forEach(line => {
    const parts = line.split('|');
    if (parts.length < 2) return;

    const itemType = parts[0].trim();
    const text = parts.slice(1).join('|').trim(); // Handle pipes in text

    // Validate item_type
    if (!['reflection', 'fact', 'todo'].includes(itemType)) {
      console.error(`Invalid item_type: ${itemType}`);
      return;
    }

    extractions.push({
      conversation_id: context.conversation_id,
      item_type: itemType,
      text: text,
      display_order: displayOrder++
    });
  });

  // If no extractions, return empty array with context
  if (extractions.length === 0) {
    return [{
      json: {
        ...context,
        extractions: [],
        has_extractions: false
      }
    }];
  }

  return [{
    json: {
      ...context,
      extractions: extractions,
      has_extractions: true
    }
  }];
} catch (error) {
  // On error, return empty extractions
  return [{
    json: {
      ...context,
      extractions: [],
      has_extractions: false,
      parse_error: error.message
    }
  }];
}

// Build SQL query to insert all extractions
const data = $('Write Thread Extraction Trace').first().json
const extractions = $('Has Extractions?').first().json.extractions;
const extractionTraceId = data.trace_id;
if (!extractions || extractions.length === 0) {
  return [];
}

// 1. Adjusted offset to 4 since we only pass 4 variables per row
const values = extractions.map((ext, idx) => {
  const offset = idx * 5; 
  return `($${offset + 1}::uuid, $${offset + 2}, $${offset + 3}, $${offset + 4}::int, $${offset + 5}::uuid)`;
}).join(', ');

const query = `
INSERT INTO thread_extractions (
  conversation_id,
  item_type,
  text,
  display_order,
  extraction_trace_id
)
VALUES ${values}
RETURNING *;
`;

// 2. The params array now correctly matches the $1, $2, $3, $4, $5... sequence
const params = [];
extractions.forEach(ext => {
  params.push(ext.conversation_id);
  params.push(ext.item_type);
  params.push(ext.text);
  params.push(ext.display_order);
  params.push(extractionTraceId);
  params.push(extractionTraceId);
});

return [{
  json: {
    ...data,
    sql_query: query,
    sql_params: params
  }
}];

// Build Discord summary message
const data = $('Parse Extractions').first().json;
const extractions = $('Has Extractions?').first().json.extractions;
const extractionTraceId = data.trace_id;

const numberEmojis = ['1️⃣', '2️⃣', '3️⃣', '4️⃣', '5️⃣', '6️⃣', '7️⃣', '8️⃣', '9️⃣', '🔟'];

const notes = extractions.filter(e => 
  e.item_type === 'reflection' || e.item_type === 'fact'
);
const todos = extractions.filter(e => e.item_type === 'todo');

let content = '📊 **Thread Summary**\n\n';

if (notes.length > 0) {
  content += '💡 **Insights & Facts**\n';
  notes.forEach(item => {
    const emoji = numberEmojis[item.display_order - 1] || '•';
    content += `${emoji} ${item.text}\n`;
  });
  content += '\n';
}

if (todos.length > 0) {
  content += '📋 **Action Items**\n';
  todos.forEach(item => {
    const emoji = numberEmojis[item.display_order - 1] || '•';
    content += `${emoji} ${item.text}\n`;
  });
  content += '\n';
}

content += '---\n';
content += 'Tap number emoji to save\n';
content += '❌ Dismiss • 🗑️ Delete thread';

return [{
  json: {
    ...data,
    summary_content: content,
    emoji_count: extractions.length
  }
}];

// Get summary message ID from Discord response
const summaryMessage = $input.first().json;
const summaryMessageId = summaryMessage.id;

const data = $('Build Summary Message').first().json;
const extractions = $('Has Extractions?').first().json.extractions;
const extractionTraceId = data.trace_id;

const numberEmojis = ['1️⃣', '2️⃣', '3️⃣', '4️⃣', '5️⃣', '6️⃣', '7️⃣', '8️⃣', '9️⃣', '🔟'];

// Build emoji reaction URLs
const reactionUrls = [];

// Add number emojis for each extraction
extractions.forEach(ext => {
  const emoji = numberEmojis[ext.display_order - 1];
  if (emoji) {
    // URL encode the emoji
    const encoded = encodeURIComponent(emoji);
    reactionUrls.push({
      url: `https://discord.com/api/v10/channels/${data.channel_id}/messages/${summaryMessageId}/reactions/${encoded}/@me`,
      emoji: emoji
    });
  }
});

// Add control emojis
reactionUrls.push(
  {
    url: `https://discord.com/api/v10/channels/${data.channel_id}/messages/${summaryMessageId}/reactions/%E2%9D%8C/@me`,
    emoji: '❌'
  },
  {
    url: `https://discord.com/api/v10/channels/${data.channel_id}/messages/${summaryMessageId}/reactions/%F0%9F%97%91%EF%B8%8F/@me`,
    emoji: '🗑️'
  }
);

return reactionUrls.map(r => ({
  json: {
    ...data,
    summary_message_id: summaryMessageId,
    reaction_url: r.url,
    emoji: r.emoji
  }
}));

// Prepare trace data for thread extraction (Option E schema)
const context = $('Set Inference Start').first().json.context;
const event = $('Execute Workflow Trigger').first().json.event;
const inferenceStart = $('Set Inference Start').first().json.inference_start;
const durationMs = Date.now() - inferenceStart;
const llmOutput = $('Extract Items LLM').first().json.text;
const parsedData = $input.first().json;

// Build filled prompt
const promptText = `You are analyzing a conversation thread to extract key insights and actionable items.

## Thread History
${context.thread_history}

## Task
Extract and classify the most important items from the thread...`;

return [{
  json: {
    // Option E schema for trace
    input_thread_history: context.thread_history,
    input_conversation_id: context.conversation_id,
    prompt_text: promptText,
    completion_text: llmOutput,
    result_extraction_count: parsedData.extractions?.length || 0,
    result_has_extractions: parsedData.has_extractions,
    duration_ms: durationMs,
    event_id: event.event_id || event.id,
    trace_chain: event.trace_chain || [],
    ...parsedData  // Pass through parsed extractions
  }
}];
