// Extract db result from Query_DB ctx
const ctx = $json.ctx;
const db = ctx.db?.projection_chain?.rows?.[0] || {};

return [{
  json: {
    ctx,
    db
  }
}];

// Format projection chain details for Discord
// ‚ùî (grey) = projections only
// ‚ùì (red) = prompt message + projections message (two separate messages)
// verbose mode = no emoji, automatic trigger, simple view
const ctx = $json.ctx;
const result = $json.db;
const emoji = ctx.event?.emoji || '‚ùî'; // Default to grey for verbose mode
const isDetailedView = emoji === '‚ùì';
const isVerboseMode = ctx.verbose === true || ctx.verbose === 'true';

// Standard emoji mapping
const typeEmojis = {
  'activity': 'üîò',
  'note': 'üìù',
  'command': 'üíª',
  'todo': 'üî≤',
  'nudge': 'üí°',
  'daily_summary': 'üìä',
  'thread_response': 'üó®Ô∏è',
  'thread_extraction': 'üîç'
};

if (!result || !result.event_id) {
  return [{
    json: {
      ctx,
      content: `${emoji} No projection data found for this message.`,
      isLast: true
    }
  }];
}

const projections = result.projections || [];
const validProjections = projections.filter(p => p && p.projection_id);

if (validProjections.length === 0) {
  return [{ json: { ctx, content: `${emoji} No saved projections.`, isLast: true } }];
}

// Build projections content (used by both views)
let projectionsContent = '';
for (const proj of validProjections) {
  const projEmoji = typeEmojis[proj.projection_type] || 'üì¶';
  let text = '';

  if (proj.projection_type === 'activity') {
    text = proj.data?.description || '(no description)';
  } else if (proj.projection_type === 'note') {
    text = proj.data?.text || '(no text)';
  } else if (proj.projection_type === 'todo') {
    text = proj.data?.description || proj.data?.text || '(no description)';
  } else if (proj.projection_type === 'nudge') {
    text = proj.data?.text || '(no text)';
  } else if (proj.projection_type === 'daily_summary') {
    text = '(daily summary)';
  } else if (proj.projection_type === 'thread_response') {
    const respText = proj.data?.response_text || '';
    text = respText.length > 60 ? respText.slice(0, 60) + '...' : respText;
  } else if (proj.projection_type === 'thread_extraction') {
    text = `[${proj.data?.item_type}] ${proj.data?.text || ''}`;
  } else {
    text = JSON.stringify(proj.data).slice(0, 60) + '...';
  }

  projectionsContent += `${projEmoji} ${text}\n`;
}
projectionsContent = projectionsContent.trim();

// === SIMPLE VIEW (‚ùî) - projections only ===
if (!isDetailedView) {
  return [{ json: { ctx, content: projectionsContent, isLast: true } }];
}

// === DETAILED VIEW (‚ùì) - prompt + projections (two messages) ===
const traces = result.traces || [];
const messageUrl = result.message_url;
const validTraces = traces.filter(t => t && t.trace_id);
const outputs = [];

// Message 1: Prompt(s)
if (validTraces.length === 0) {
  outputs.push({ json: { ctx, content: '(no prompt found)', isLast: false } });
} else {
  for (let i = 0; i < validTraces.length; i++) {
    const trace = validTraces[i];
    const isLastTrace = i === validTraces.length - 1;

    let content = '';

    // Show full prompt
    if (trace.prompt) {
      let promptText = trace.prompt;

      // Check if it's JSON (old format) or full text (new format)
      try {
        const parsed = JSON.parse(promptText);
        if (parsed.text) {
          promptText = parsed.text;
        } else {
          const lines = [];
          if (parsed.timestamp) lines.push(`Time: ${parsed.timestamp}`);
          if (parsed.north_star) lines.push(`North star: ${parsed.north_star}`);
          if (parsed.recent_activities) lines.push(`Recent activities:\n${parsed.recent_activities}`);
          if (parsed.last_note) lines.push(`Last note: ${parsed.last_note}`);
          promptText = lines.join('\n') || JSON.stringify(parsed, null, 2);
        }
      } catch {
        // Already a string (new format) - use as-is
      }

      // Truncate if too long
      if (promptText.length > 1800) {
        promptText = promptText.slice(0, 1800) + '\n... (truncated)';
      }

      content += '```\n' + promptText + '\n```';
    }

    // Duration and link after prompt
    let footer = '';
    if (trace.duration_ms) {
      footer += `${trace.duration_ms}ms`;
    }
    if (messageUrl && i === 0) {
      footer += footer ? ' ' : '';
      footer += `[‚Üí](${messageUrl})`;
    }
    if (footer) {
      content += '\n' + footer;
    }

    // This is NOT the last message - projections come after
    outputs.push({ json: { ctx, content: content.trim(), isLast: false } });
  }
}

// Message 2: Projections (last message)
outputs.push({ json: { ctx, content: projectionsContent, isLast: true } });

return outputs;

// Restore ctx after Discord node (which overwrites $json)
const formatOutput = $('Format Details').all();
const currentIndex = $itemIndex;
const item = formatOutput[currentIndex]?.json || {};
const ctx = item.ctx || {};

return [{
  json: {
    ctx: ctx,
    isLast: item.isLast,
    isVerboseMode: ctx.verbose === true || ctx.verbose === 'true' || !ctx.event?.emoji
  }
}];
