// Get event from trigger
const event = $('Execute Workflow Trigger').first().json.event;

// Access thread creation result (first input to merge)
const threadResult = $input.first().json;
const threadId = threadResult.id;

// Access north star result (second input to merge)
const northStarItems = $input.all();
let northStar = 'Not set';
const northStarResult = northStarItems.find(item => item.json.value !== undefined);
if (northStarResult) {
  northStar = northStarResult.json.value;
}

return [{
  json: {
    ...event,
    north_star: northStar,
    thread_id: threadId,
  }
}];

// Prepare trace data after LLM inference (Option E schema)
const context = $('Set Inference Start').first().json.context;
const event = $('Execute Workflow Trigger').first().json.event;
const inferenceStart = $('Set Inference Start').first().json.inference_start;
const durationMs = Date.now() - inferenceStart;
const response = $input.first().json.text;

// Build filled prompt (reconstructed from template) with fallbacks
const northStar = context.north_star || '(not set)';
const cleanText = context.clean_text || '';
const promptText = `You are an AI life coach helping the user reflect, plan, and think deeply.

## Context Available

**User's Guiding Principle:** ${northStar}

Keep this in mind when helping them, but don't mention it unless directly relevant to the conversation.

## Current Situation

The user just started a conversation thread with: ${cleanText}

They are coming to you for help thinking through something.

## Instructions
...

Respond to their initial message now.`;

// Format trace_chain for PostgreSQL uuid[] type
const traceChain = event.trace_chain || [];
const traceChainPg = '{' + traceChain.join(',') + '}';

return [{
  json: {
    // Option E schema for trace
    input_text: context.clean_text || '',
    input_conversation_id: context.thread_id,
    prompt_text: promptText,
    completion_text: response,
    result_response_length: response.length,
    duration_ms: durationMs,
    event_id: event.event_id,
    trace_chain_pg: traceChainPg,
    ...context  // Pass through all context fields
  }
}];
