// Prepare db_queries for trace + projection storage
const ctx = $json.ctx || {};

const response = ctx.llm?.completion_text || '';
const inferenceStart = ctx.llm?.inference_start || Date.now();
const durationMs = Date.now() - inferenceStart;

// Build filled prompt (reconstructed from template)
const northStar = ctx.llm?.north_star || '(not set)';
const cleanText = ctx.event?.clean_text || '';
const promptText = `You are an AI life coach...\n\nUser's Guiding Principle: ${northStar}\n\nThe user started with: ${cleanText}`;

// Prepare db_queries for Execute_Queries
const db_queries = [
  {
    key: 'trace',
    sql: `WITH new_trace AS (
            INSERT INTO traces (event_id, step_name, data, trace_chain)
            VALUES ($1::uuid, 'thread_initial_response',
              jsonb_build_object(
                'input', jsonb_build_object('text', $2, 'thread_id', $3::text),
                'prompt', $4,
                'completion', $5,
                'result', jsonb_build_object('response_length', $6::integer),
                'model', 'nvidia/nemotron-nano-9b-v2:free',
                'duration_ms', $7::integer
              ), $8::uuid[])
            RETURNING id
          )
          SELECT new_trace.id as trace_id, $8::uuid[] || new_trace.id as updated_trace_chain
          FROM new_trace`,
    params: [
      ctx.event?.event_id,
      cleanText,
      ctx.thread?.id,
      promptText,
      response,
      response.length,
      durationMs,
      ctx.event?.trace_chain || []
    ]
  },
  {
    key: 'projection',
    sql: `INSERT INTO projections (event_id, trace_id, trace_chain, projection_type, status, data, timezone)
          VALUES ($1::uuid, $2::uuid, $3::uuid[], 'thread_response', 'auto_confirmed',
            jsonb_build_object(
              'thread_id', $4,
              'response_text', $5,
              'role', 'assistant',
              'timestamp', $6::timestamptz
            ), $7)
          RETURNING id`,
    params: [
      ctx.event?.event_id,
      '$results.trace.row.trace_id',
      '$results.trace.row.updated_trace_chain',
      ctx.thread?.id,
      response,
      ctx.event?.timestamp,
      ctx.event?.timezone
    ]
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    }
  }
}];

// Prepare north_star query and build initial ctx
const triggerData = $json;
const ctx = triggerData.ctx || {};

const db_queries = [
  {
    key: 'north_star',
    sql: "SELECT value FROM config WHERE key = 'north_star'"
  }
];

return [{
  json: {
    ctx: {
      ...ctx,
      db_queries
    }
  }
}];

// Build context for LLM from thread and db results
const ctx = $json.ctx || {};
const threadData = $('Create Discord Thread').first().json;

// Get north_star from Execute_Queries result
const northStar = ctx.db?.north_star?.row?.value || '(not set)';

// Preserve all ctx.event fields and add thread + llm context
return [{
  json: {
    ctx: {
      event: {
        event_id: ctx.event?.event_id,
        channel_id: ctx.event?.channel_id,
        message_id: ctx.event?.message_id,
        guild_id: ctx.event?.guild_id,
        clean_text: ctx.event?.clean_text,
        timestamp: ctx.event?.timestamp,
        timezone: ctx.event?.timezone,
        trace_chain: ctx.event?.trace_chain
      },
      thread: {
        id: threadData.id
      },
      db: {
        north_star: northStar
      },
      llm: {
        inference_start: Date.now(),
        north_star: northStar
      }
    }
  }
}];

// Add LLM response to ctx
const prevData = $('Build LLM Context').first().json;
const ctx = prevData.ctx;
const llmResponse = $json.text || '';

return [{
  json: {
    ctx: {
      ...ctx,
      llm: {
        ...ctx.llm,
        completion_text: llmResponse
      }
    }
  }
}];
